const pg = require('pg');
const { Pool } = pg;

// Single shared pool across all serverless invocations in this runtime
let pool;

function getPool() {
  if (!pool) {
    const connectionString = process.env.POSTGRES_URL || process.env.DATABASE_URL;
    if (!connectionString) {
      throw new Error('POSTGRES_URL is not set');
    }

    pool = new Pool({
      connectionString,
      ssl: process.env.POSTGRES_SSL === 'true' ? { rejectUnauthorized: false } : false,
      max: Number(process.env.PG_POOL_MAX || 5),
      idleTimeoutMillis: Number(process.env.PG_IDLE_TIMEOUT || 30000)
    });
  }
  return pool;
}

async function query(text, params) {
  const client = await getPool().connect();
  try {
    const start = Date.now();
    const res = await client.query(text, params);
    const duration = Date.now() - start;
    if (process.env.LOG_SQL === 'true') {
      console.log('executed query', { text, duration, rows: res.rowCount });
    }
    return res;
  } finally {
    client.release();
  }
}

module.exports = { getPool, query };

// Simple structured logger
// Usage: log.info('message', { contextKey: 'value' })
const levels = ['debug', 'info', 'warn', 'error'];

function base(fields) {
  const ts = new Date().toISOString();
  return { ts, ...fields };
}

function serialize(obj) {
  try { return JSON.stringify(obj); } catch { return '{"error":"log_serialize_failed"}'; }
}

const log = {};
levels.forEach(level => {
  log[level] = (msg, meta = {}) => {
    // Avoid logging undefined / circular
    const line = base({ level, msg, ...meta });
    if (level === 'error') {
      // eslint-disable-next-line no-console
      console.error(serialize(line));
    } else if (level === 'warn') {
      // eslint-disable-next-line no-console
      console.warn(serialize(line));
    } else {
      // eslint-disable-next-line no-console
      console.log(serialize(line));
    }
  };
});

module.exports = { log };

// Simple in-memory rate limiter (per key) for low-volume deployments.
// Not suitable for multi-instance horizontal scaling (would need Redis or similar).

const buckets = new Map();

function rateLimit({ windowMs = 60_000, max = 30, keyGenerator } = {}) {
  return (req, res, next) => {
    try {
      const now = Date.now();
      const key = keyGenerator ? keyGenerator(req) : (req.headers['x-forwarded-for'] || req.socket.remoteAddress || 'unknown');
      if (!buckets.has(key)) {
        buckets.set(key, []);
      }
      const arr = buckets.get(key);
      // prune
      while (arr.length && (now - arr[0]) > windowMs) arr.shift();
      if (arr.length >= max) {
        res.status(429).setHeader('content-type','application/json').end(JSON.stringify({ error: 'Rate limit exceeded' }));
        return;
      }
      arr.push(now);
      next();
    } catch (e) {
      next(); // fail-open
    }
  };
}

module.exports = { rateLimit };

function json(res, body, status = 200) {
  res.status(status).setHeader('content-type', 'application/json; charset=utf-8');
  res.end(JSON.stringify(body));
}

function error(res, message, status = 400, extra = {}) {
  json(res, { error: message, ...extra }, status);
}

function resolveBaseUrl() {
  // Priority order:
  // 1. Explicit SITE_BASE_URL (full origin, e.g. https://example.com)
  // 2. VERCEL_URL (host only) -> https://VERCEL_URL
  // 3. Render/Netlify style PUBLIC_BASE_URL if later added
  // 4. Fallback localhost (protocol required by URL API)
  if (process.env.SITE_BASE_URL) return process.env.SITE_BASE_URL.replace(/\/$/, '');
  if (process.env.VERCEL_URL) return `https://${process.env.VERCEL_URL.replace(/\/$/, '')}`;
  if (process.env.PUBLIC_BASE_URL) return process.env.PUBLIC_BASE_URL.replace(/\/$/, '');
  return 'http://localhost';
}

function getUrl(req) {
  const base = resolveBaseUrl();
  try {
    return new URL(req.url, base);
  } catch {
    const query = req.query || {};
    const params = new URLSearchParams(query);
    return new URL(`${req.url}?${params.toString()}`, base);
  }
}

// Build a canonical absolute URL for SEO (path should start with '/' or be a URL object/string)
function buildCanonicalUrl(pathOrUrl) {
  const base = resolveBaseUrl();
  let target;
  try {
    if (typeof pathOrUrl === 'string' && !/^https?:\/\//i.test(pathOrUrl)) {
      // Ensure leading slash for relative paths
      const p = pathOrUrl.startsWith('/') ? pathOrUrl : '/' + pathOrUrl;
      target = new URL(p, base).toString();
    } else {
      target = new URL(pathOrUrl, base).toString();
    }
  } catch {
    target = base; // fallback
  }
  return target.replace(/\/$/, '');
}

// Emit a runtime warning if production uses localhost base (misconfiguration)
if (process.env.NODE_ENV === 'production') {
  const b = resolveBaseUrl();
  if (/localhost(:\d+)?$/.test(b)) {
    // eslint-disable-next-line no-console
    console.warn('[config-warning] SITE_BASE_URL/VERCEL_URL not set; canonical URLs will use localhost.');
  }
}

module.exports = { json, error, getUrl, resolveBaseUrl, buildCanonicalUrl };

const crypto = require('crypto');
const { camelize } = require('./keys.js');

function computeEtag(payload) {
  return 'W/"' + crypto.createHash('sha1').update(payload).digest('base64').slice(0,27) + '"';
}

// Wrap a serverless handler to post-process JSON responses into camelCase and add ETag caching.
function withTransform(handler, { camel = true, cacheSeconds = 0 } = {}) {
  return async (req, res) => {
    const originalJson = res.json;
    const originalEnd = res.end;
    let bodyBuffer = null;
    // Monkey patch
    res.json = function (obj) {
      let payloadObj = obj;
      if (camel && obj && typeof obj === 'object') {
        if (Array.isArray(obj)) payloadObj = obj.map(o => (o && typeof o === 'object' && !Array.isArray(o)) ? camelize(o, true) : o);
        else payloadObj = camelize(obj, true);
      }
      const jsonStr = JSON.stringify(payloadObj);
      bodyBuffer = Buffer.from(jsonStr);
      const etag = computeEtag(jsonStr);
      res.setHeader('ETag', etag);
      if (cacheSeconds > 0) {
        res.setHeader('Cache-Control', `public, max-age=${cacheSeconds}`);
      } else {
        res.setHeader('Cache-Control', 'no-store');
      }
      // Conditional request support
      if (req.headers['if-none-match'] === etag) {
        res.statusCode = 304;
        return originalEnd.call(res);
      }
      return originalJson.call(res, payloadObj);
    };
    return handler(req, res);
  };
}

module.exports = { withTransform };

// Minimal HTML sanitizer (simple whitelist). For stronger needs, add `sanitize-html` dependency.
// Allows basic formatting tags and strips event handlers / scripts.

const BASE_ALLOWED_TAGS = ['b','i','em','strong','p','br','ul','ol','li','pre','code','blockquote','h1','h2','h3','h4','h5','h6','a'];
const BASE_ALLOWED_ATTR = { a: ['href','title','target','rel'] };

function sanitize(html = '', opts = {}) {
  if (!html || typeof html !== 'string') return '';
  const allowVideo = !!opts.allowVideo;
  const ALLOWED_TAGS = allowVideo ? [...BASE_ALLOWED_TAGS, 'iframe'] : BASE_ALLOWED_TAGS;
  const ALLOWED_ATTR = { ...BASE_ALLOWED_ATTR };
  if (allowVideo) ALLOWED_ATTR.iframe = ['src','title','allow','allowfullscreen','width','height','frameborder'];
  // Remove script/style tags entirely
  let out = html.replace(/<\/(script|style)>/gi,'').replace(/<(script|style)[^>]*>[\s\S]*?<\/\1>/gi,'');
  // Remove on* handlers and javascript: URLs
  out = out.replace(/on\w+\s*=\s*"[^"]*"/gi,'')
           .replace(/on\w+\s*=\s*'[^']*'/gi,'')
           .replace(/on\w+\s*=\s*[^\s>]+/gi,'')
           .replace(/javascript:/gi,'');
  // Strip tags not in allow list
  out = out.replace(/<([^>\/\s]+)([^>]*)>/gi, (m, tag, attrs) => {
    const t = tag.toLowerCase();
    if (!ALLOWED_TAGS.includes(t)) return '';
    if (!attrs) return `<${t}>`;
    const rawAttrs = (attrs.match(/\s+[a-zA-Z:-]+\s*=\s*("[^"]*"|'[^']*'|[^\s>]+)/g) || []);
    let cleanAttrs = rawAttrs
      .map(a => a.trim())
      .filter(a => {
        const eq = a.indexOf('=');
        if (eq === -1) return false;
        const name = a.slice(0, eq).toLowerCase();
        if (!ALLOWED_ATTR[t]) return false;
        if (!ALLOWED_ATTR[t].includes(name)) return false;
        return true;
      })
      .join(' ');
    // Additional src whitelist for iframes (YouTube/Vimeo only)
    if (t === 'iframe') {
      const srcMatch = /src=("([^"]*)"|'([^']*)')/.exec(attrs);
      const srcVal = srcMatch ? (srcMatch[2] || srcMatch[3] || '') : '';
      const allowed = /^(https:\/\/(www\.)?(youtube\.com|youtu\.be|player\.vimeo\.com)\/)/i.test(srcVal);
      if (!allowed) return ''; // strip entire iframe if not whitelisted
      // Force rel="noopener" not relevant for iframe; ensure no javascript: encoded
        // Sanitize allow attribute to a curated subset
        cleanAttrs = cleanAttrs.replace(/allow=("[^"]*"|'[^']*')/gi, (m) => {
          const val = m.split('=')[1].replace(/^["']|["']$/g,'');
          const features = val.split(/;|,/).map(v => v.trim().toLowerCase());
          const allowedFeatures = ['accelerometer','autoplay','clipboard-write','encrypted-media','gyroscope','picture-in-picture'];
          const filtered = features.filter(f => allowedFeatures.includes(f));
          return filtered.length ? `allow="${filtered.join('; ')}"` : '';
        });
    }
    return `<${t}${cleanAttrs ? ' ' + cleanAttrs : ''}>`;
  });
  // Remove orphan closing tags for disallowed iframes or others (simple pass)
  if (!allowVideo) {
    out = out.replace(/<\/iframe>/gi, '');
  }
  // Close tags that are self-closing incorrectly not handled here (lightweight)
  return out;
}

module.exports = { sanitize };

const { json, error, getUrl } = require('../_lib/respond.js');
const { query } = require('../_lib/db.js');
const { requireAdmin } = require('../_lib/auth.js');
