// POST /api/settings/features { flag_key, enabled?, note? } -> upsert (admin)
module.exports = async function handler(req, res) {
  try {
    if (req.method === 'GET') {
      const { rows } = await query('SELECT flag_key, enabled, note, updated_at FROM wisdomintech.feature_flags ORDER BY flag_key');
      return json(res, rows);
    }
    if (req.method === 'POST') {
      const user = await requireAdmin(req, res);
      if (!user) return; // auth helper already responded
      const body = await getJsonBody(req);
      const key = body.flag_key || body.key;
      if (!key) return error(res, 'flag_key required', 400);
      const enabled = typeof body.enabled === 'boolean' ? body.enabled : true;
      const note = body.note || null;
      const upsertSql = `
        INSERT INTO wisdomintech.feature_flags (flag_key, enabled, note)
        VALUES ($1,$2,$3)
        ON CONFLICT (flag_key)
        DO UPDATE SET enabled = EXCLUDED.enabled, note = COALESCE(EXCLUDED.note, wisdomintech.feature_flags.note), updated_at = now()
        RETURNING flag_key, enabled, note, updated_at
      `;
      const { rows } = await query(upsertSql, [key, enabled, note]);
      return json(res, rows[0], 201);
    }
    return error(res, 'Method not allowed', 405);
  } catch (e) {
    return error(res, 'Failed to process feature flags request', 500, { detail: e.message });
  }
};

const { requireAdmin } = require("../_lib/auth");
const respond = require("../_lib/respond");
const db = require("../_lib/db");
const fs = require("fs");
const path = require("path");

module.exports = async function handler(req, res) {
  if (req.method !== "PUT") return respond.methodNotAllowed(res);
  const user = await requireAdmin(req, res);
  if (!user) return;
  try {
    const { image } = req.body || {};
    if (!image || typeof image !== "string")
      return respond.badRequest(res, "Missing image");

    let finalUrl = null;
    if (/^data:image\//i.test(image)) {
      // data URI
      const m = image.match(
        /^data:(image\/(?:png|jpeg|jpg|webp));base64,(.+)$/i,
      );
      if (!m) return respond.badRequest(res, "Invalid data URI");
      const ext = m[1].split("/")[1].replace("jpeg", "jpg");
      const buf = Buffer.from(m[2], "base64");
      const fileName = "og-default." + ext;
      const outDir = path.join(process.cwd(), "public", "assets", "images");
      if (!fs.existsSync(outDir)) fs.mkdirSync(outDir, { recursive: true });
      const outPath = path.join(outDir, fileName);
      fs.writeFileSync(outPath, buf);
      finalUrl = "/assets/images/" + fileName + "?v=" + Date.now();
    } else if (/^https?:\/\//i.test(image)) {
      finalUrl = image;
    } else {
      return respond.badRequest(
        res,
        "Image must be https URL or base64 data URI",
      );
    }

    await db.query(
      `UPDATE site_settings SET og_default_image_url = $1, updated_at = now() WHERE id = 1`,
      [finalUrl],
    );
    return respond.ok(res, { og_default_image_url: finalUrl });
  } catch (e) {
    return respond.error(res, "Failed to update OG image");
  }
};

