class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    error.__ErrorBoundary = true;
    window.__COMPONENT_ERROR__?.(error, errorInfo);
    // console.log("Error caught by ErrorBoundary:", error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="min-h-screen flex items-center justify-center bg-neutral-50">
          <div cl assName="text-center p-8 max-w-md">
            <div className="flex justify-center items-center mb-2">
              <svg xmlns="http://www.w3.org/2000/svg" width="42px" height="42px" viewBox="0 0 32 33" fill="none">
                <path d="M16 28.5C22.6274 28.5 28 23.1274 28 16.5C28 9.87258 22.6274 4.5 16 4.5C9.37258 4.5 4 9.87258 4 16.5C4 23.1274 9.37258 28.5 16 28.5Z" stroke="#343330" strokeWidth="2" strokeMiterlimit="10" />
                <path d="M11.5 15.5C12.3284 15.5 13 14.8284 13 14C13 13.1716 12.3284 12.5 11.5 12.5C10.6716 12.5 10 13.1716 10 14C10 14.8284 10.6716 15.5 11.5 15.5Z" fill="#343330" />
                <path d="M20.5 15.5C21.3284 15.5 22 14.8284 22 14C22 13.1716 21.3284 12.5 20.5 12.5C19.6716 12.5 19 13.1716 19 14C19 14.8284 19.6716 15.5 20.5 15.5Z" fill="#343330" />
                <path d="M21 22.5C19.9625 20.7062 18.2213 19.5 16 19.5C13.7787 19.5 12.0375 20.7062 11 22.5" stroke="#343330" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
              </svg>
            </div>
            <div className="flex flex-col gap-1 text-center">
              <h1 className="text-2xl font-medium text-neutral-800">Something went wrong</h1>
              <p className="text-neutral-600 text-base w w-8/12 mx-auto">We encountered an unexpected error while processing your request.</p>
            </div>
            <div className="flex justify-center items-center mt-6">
              <button
                onClick={() => {
                  window.location.href = "/";
                }}
                className="bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded flex items-center gap-2 transition-colors duration-200 shadow-sm"
              >
                <Icon name="ArrowLeft" size={18} color="#fff" />
                Back
              </button>
            </div>
          </div >
        </div >
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;

import React, { useEffect, useState } from 'react';
import { simpleLineDiff } from '../../utils/diff';

export default function RevisionDiffViewer({ revisionId, onClose }) {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [diffRows, setDiffRows] = useState([]);
  const base = import.meta.env.VITE_API_BASE_URL || '/api';

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        setLoading(true); setError(null);
        const res = await fetch(base + '/blog/revision-diff?revisionId=' + revisionId);
        if (!res.ok) throw new Error(await res.text());
        const data = await res.json();
        if (!mounted) return;
        const diff = simpleLineDiff(data.before.content || '', data.after.content || '');
        setDiffRows(diff.slice(0, 4000)); // safety cap
      } catch (e) {
        if (mounted) setError('Failed to load diff');
      } finally {
        if (mounted) setLoading(false);
      }
    })();
    return () => { mounted = false; };
  }, [revisionId]);

  return (
    <div className="fixed inset-0 z-50 flex flex-col bg-background/90 backdrop-blur p-6 overflow-hidden">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-lg font-semibold">Revision Diff</h2>
        <button onClick={onClose} className="px-3 py-1 rounded bg-error text-background text-sm">Close</button>
      </div>
      {loading && <div className="text-sm text-text-secondary">Loading</div>}
      {error && <div className="text-sm text-error mb-2">{error}</div>}
      <div className="flex-1 overflow-auto rounded border border-border-accent/30 bg-surface text-xs font-mono">
        {diffRows.map((r, i) => (
          <div key={i} className={
            r.type === 'context' ? 'flex px-2 py-0.5' : r.type === 'add' ? 'flex px-2 py-0.5 bg-success/10' : 'flex px-2 py-0.5 bg-error/10'
          }>
            <div className="w-1/2 pr-2 whitespace-pre-wrap break-words border-r border-border-accent/20">
              {r.before}
            </div>
            <div className="w-1/2 pl-2 whitespace-pre-wrap break-words">
              {r.after}
            </div>
          </div>
        ))}
        {!loading && diffRows.length === 0 && <div className="p-4 text-text-secondary">No differences.</div>}
      </div>
    </div>
  );
}

