import React from 'react';
import { Helmet } from 'react-helmet';
import { useLocation } from 'react-router-dom';
import { getCanonicalUrl } from '../../utils/canonical';

// Canonical tag injector. Place once near root so every route updates tag.
export default function Canonical() {
  const location = useLocation();
  // External flag (window.__SUPPRESS_CANONICAL) can be toggled by routes like 404.
  const suppress = typeof window !== 'undefined' && window.__SUPPRESS_CANONICAL;
  if (suppress) return null;
  const canonical = getCanonicalUrl(location.pathname + location.search);
  return (
    <Helmet>
      <link rel="canonical" href={canonical} />
    </Helmet>
  );
}

import React from 'react';
import Icon from 'components/AppIcon';

const Button = React.forwardRef(({
    children,
    variant = 'primary',
    size = 'md',
    shape = 'rounded',
    fullWidth = false,
    disabled = false,
    loading = false,
    icon = null,
    iconName = null,
    iconPosition = 'left',
    type = 'button',
    iconSize = null,
    iconColor = null,
    className = '',
    onClick,
    ...rest
}, ref) => {
    // Base classes
    const baseClasses = 'inline-flex items-center justify-center transition-all duration-200 font-medium focus:ring-2 focus:outline-none';

    // Size classes
    const sizeClasses = {
        '2xs': 'text-xs py-0.5 px-1.5',
        xs: 'text-xs py-1 px-2',
        sm: 'text-sm py-1.5 px-3',
        md: 'text-base py-2 px-4',
        lg: 'text-lg py-2.5 px-5',
        xl: 'text-xl py-3 px-6',
        '2xl': 'text-2xl py-4 px-8',
    };

    // Shape classes
    const shapeClasses = {
        rounded: 'rounded',
        square: 'rounded-none',
        pill: 'rounded-full',
        circle: 'rounded-full aspect-square',
    };

    // Variant classes
    const variantClasses = {
        primary: 'bg-primary text-primary-foreground hover:bg-primary/90',
        secondary: 'bg-secondary text-secondary-foreground hover:bg-secondary/80',
        success: 'bg-success text-white hover:bg-opacity-90 active:bg-opacity-100 disabled:opacity-50',
        danger: 'bg-error text-white hover:bg-opacity-90 active:bg-opacity-100 disabled:opacity-50',
        warning: 'bg-warning text-primary-foreground hover:bg-opacity-90 active:bg-opacity-100 disabled:opacity-50',
        info: 'bg-accent text-white hover:bg-opacity-90 active:bg-opacity-100 disabled:opacity-50',
        ghost: 'hover:bg-primary hover:text-primary-foreground',
        link: 'bg-transparent text-primary-foreground underline hover:text-primary-600 p-0',
        outline: 'border border-input bg-background hover:bg-primary hover:text-primary-foreground',
        text: 'bg-transparent text-primary-foreground hover:bg-surface hover:bg-opacity-50 active:bg-surface active:bg-opacity-70',
    };


    // Width classes
    const widthClasses = fullWidth ? 'w-full' : '';

    // Disabled classes
    const disabledClasses = disabled ? 'cursor-not-allowed opacity-60' : '';

    // Loading state
    const loadingContent = loading ? (
        <svg className="animate-spin -ml-1 mr-2 h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
        </svg>
    ) : null;

    // Icon rendering
    const renderIcon = () => {
        if (iconName) {
            // Use AppIcon component when iconName is provided
            const iconSizeMap = {
                '2xs': 12,
                xs: 14,
                sm: 16,
                md: 18,
                lg: 20,
                xl: 22,
                '2xl': 24,
            };

            const calculatedSize = iconSize || iconSizeMap[size] || 18;

            return (
                <span style={{ color: iconColor || 'currentColor' }}>
                    <Icon
                        name={iconName}
                        size={calculatedSize}
                        className={`${children ? (iconPosition === 'left' ? 'mr-2' : 'ml-2') : ''}`}
                    />
                </span>

            );
        }

        if (!icon) return null;

        return React.cloneElement(icon, {
            className: `${children ? (iconPosition === 'left' ? 'mr-2' : 'ml-2') : ''} h-5 w-5`
        });
    };

    // Combine all classes
    const classes = `
                    ${baseClasses}
                    ${sizeClasses[size] || sizeClasses.md}
                    ${shapeClasses[shape] || shapeClasses.rounded}
                    ${variantClasses[variant] || variantClasses.primary}
                    ${widthClasses}
                    ${disabledClasses}
                    ${className}
                    `;

    return (
        <button
            ref={ref}
            type={type}
            className={classes}
            disabled={disabled || loading}
            onClick={onClick}
            {...rest}
        >
            {loading && loadingContent}
            {(icon || iconName) && iconPosition === 'left' && renderIcon()}
            {children}
            {(icon || iconName) && iconPosition === 'right' && renderIcon()}
        </button>
    );
});

export default Button;

import React, { useRef, useState } from 'react';
import Button from './Button';
import Icon from '../AppIcon';
import storageService from '../../utils/storageService';

const ACCEPT_MAP = {
  image: 'image/*',
  icon: '.ico,image/x-icon,image/vnd.microsoft.icon,image/png,image/svg+xml',
  video: 'video/*',
  document: '.pdf,.doc,.docx',
};

const FileUpload = ({
  label = 'Upload file',
  value,
  onChange,
  bucket = 'media',
  pathPrefix = 'uploads',
  accept = 'image',
  disabled = false,
  helperText,
}) => {
  const inputRef = useRef(null);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState('');

  const handlePick = () => inputRef.current?.click();

  const handleFile = async (file) => {
    if (!file) return;
    setError('');
    setUploading(true);
    const res = await storageService.uploadFile(file, { bucket, pathPrefix });
    setUploading(false);
    if (!res.success) {
      setError(res.error || 'Upload failed');
      return;
    }
    onChange?.(res.data.url, res.data);
  };

  const onInputChange = (e) => {
    const file = e.target.files?.[0];
    if (file) handleFile(file);
    e.target.value = '';
  };

  const onDrop = (e) => {
    e.preventDefault();
    if (disabled || uploading) return;
    const file = e.dataTransfer?.files?.[0];
    if (file) handleFile(file);
  };

  return (
    <div>
      {label && <label className="block text-sm font-medium text-text-primary mb-2">{label}</label>}
      <div
        onDragOver={(e) => e.preventDefault()}
        onDrop={onDrop}
        className={`flex items-center gap-3 p-3 border rounded-lg bg-surface ${uploading ? 'opacity-70' : ''} border-border-accent/20`}
      >
        <input ref={inputRef} type="file" accept={ACCEPT_MAP[accept] || accept} className="hidden" onChange={onInputChange} />
        <div className="w-24 h-16 rounded bg-background/40 border border-border-accent/20 flex items-center justify-center overflow-hidden">
          {value ? (
            (() => {
              const isVideo = (accept === 'video') || /\.(mp4|webm|ogg)(\?|#|$)/i.test(String(value));
              const isDocument = (accept === 'document') || /\.(pdf|doc|docx)(\?|#|$)/i.test(String(value));
              if (isVideo) {
                return (
                  <video
                    src={value}
                    className="w-full h-full object-cover"
                    muted
                    playsInline
                    loop
                  />
                );
              }
              if (isDocument) {
                return (
                  <Icon name="FileText" size={20} className="text-primary" />
                );
              }
              return (
                // eslint-disable-next-line @next/next/no-img-element
                <img src={value} alt="preview" className="w-full h-full object-cover" />
              );
            })()
          ) : (
            <Icon name={accept === 'video' ? 'Video' : accept === 'document' ? 'FileText' : 'Image'} size={20} className="text-text-secondary" />
          )}
        </div>
        <div className="flex-1 min-w-0">
          <div className="text-xs text-text-secondary truncate">{value || 'No file selected'}</div>
          {helperText && <div className="text-[11px] text-text-secondary/70">{helperText}</div>}
          {error && <div className="text-[11px] text-error mt-1">{error}</div>}
        </div>
        <div className="flex items-center gap-2">
          <Button type="button" size="sm" variant="outline" onClick={handlePick} disabled={disabled || uploading}>
            {uploading ? 'Uploading' : 'Choose File'}
          </Button>
          {value && (
            <a href={value} target="_blank" rel="noreferrer" className="text-xs text-primary hover:underline">Open</a>
          )}
        </div>
      </div>
    </div>
  );
};

export default FileUpload;

