import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../utils/supabase';
import Input from '../components/ui/Input';
import Button from '../components/ui/Button';
import Icon from '../components/AppIcon';

const ResetPassword = () => {
  const navigate = useNavigate();
  const [status, setStatus] = useState('init'); // init | ready | error | done
  const [error, setError] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        // Exchange recovery token for a session if present in URL
        const { data, error } = await supabase.auth.exchangeCodeForSession(window.location.href);
        if (error) {
          console.log('exchangeCodeForSession error:', error);
          if (mounted) {
            setStatus('error');
            setError('Invalid or expired reset link. Please request a new one.');
          }
          return;
        }
        if (mounted) setStatus('ready');
      } catch (e) {
        if (mounted) {
          setStatus('error');
          setError('Something went wrong initializing password reset.');
        }
      }
    })();
    return () => { mounted = false; };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!password || password.length < 8) {
      setError('Password must be at least 8 characters long.');
      return;
    }
    if (password !== confirm) {
      setError('Passwords do not match.');
      return;
    }
    try {
      const { error } = await supabase.auth.updateUser({ password });
      if (error) {
        setError(error.message || 'Failed to update password.');
        return;
      }
      setStatus('done');
      setTimeout(() => navigate('/admin-dashboard-content-management'), 1500);
    } catch (e) {
      setError('Unexpected error while updating password.');
    }
  };

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-6">
      <div className="w-full max-w-md bg-surface border border-border-accent/20 rounded-lg p-6 shadow-glow-primary">
        <div className="text-center mb-6">
          <div className="w-16 h-16 bg-primary/20 rounded-full flex items-center justify-center mx-auto mb-4">
            <Icon name="Lock" size={24} className="text-primary" />
          </div>
          <h1 className="text-2xl font-heading font-bold text-text-primary">Reset Password</h1>
          <p className="text-text-secondary text-sm mt-1">Set a new password for your admin account.</p>
        </div>

        {status === 'init' && (
          <div className="text-center text-text-secondary text-sm">Preparing reset</div>
        )}

        {status === 'error' && (
          <div className="text-error text-sm mb-4">{error}</div>
        )}

        {status === 'ready' && (
          <form onSubmit={handleSubmit} className="space-y-4">
            {error && <div className="text-error text-sm">{error}</div>}
            <div>
              <label className="block text-sm font-medium text-text-primary mb-2">New Password</label>
              <Input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Enter new password" autoFocus />
            </div>
            <div>
              <label className="block text-sm font-medium text-text-primary mb-2">Confirm Password</label>
              <Input type="password" value={confirm} onChange={(e) => setConfirm(e.target.value)} placeholder="Re-enter new password" />
            </div>
            <Button type="submit" variant="primary" className="w-full">Update Password</Button>
          </form>
        )}

        {status === 'done' && (
          <div className="text-success text-center text-sm">Password updated. Redirecting</div>
        )}
      </div>
    </div>
  );
};

export default ResetPassword;

import React, { useEffect, useState } from 'react';
import HeaderNavigation from 'components/ui/HeaderNavigation';
import useFeature, { resetAllFeatureOverrides } from 'hooks/useFeature';

export default function FeatureFlagsPage() {
  const adminGateEnabled = useFeature('feature_flags_admin', true); // always true until gated separately
  const [flags, setFlags] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [saving, setSaving] = useState(null); // flag key currently saving
  const [creating, setCreating] = useState(false);
  const [newFlag, setNewFlag] = useState({ key: '', note: '', enabled: true });
  const [editingNoteKey, setEditingNoteKey] = useState(null);
  const [noteDraft, setNoteDraft] = useState('');
  const [overrideKeys, setOverrideKeys] = useState(() => {
    if (typeof window === 'undefined') return [];
    try {
      const raw = localStorage.getItem('featureFlagOverrides:v1');
      if (!raw) return [];
      return Object.keys(JSON.parse(raw));
    } catch { return []; }
  });

  function refreshOverrideKeys() {
    if (typeof window === 'undefined') return;
    try {
      const raw = localStorage.getItem('featureFlagOverrides:v1');
      setOverrideKeys(raw ? Object.keys(JSON.parse(raw)) : []);
    } catch { /* ignore */ }
  }

  async function load() {
    setLoading(true); setError(null);
    try {
      const res = await fetch((import.meta.env.VITE_API_BASE_URL || '/api') + '/settings/features');
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      setFlags(data);
      refreshOverrideKeys();
    } catch (e) {
      setError(e.message || 'Failed to load flags');
    } finally { setLoading(false); }
  }

  useEffect(() => { load(); }, []);

  async function upsertFlag({ key, enabled, note }) {
    setSaving(key);
    try {
      const res = await fetch((import.meta.env.VITE_API_BASE_URL || '/api') + '/settings/features', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ flag_key: key, enabled, note })
      });
      if (!res.ok) throw new Error(await res.text());
      const updated = await res.json();
      setFlags(f => {
        const exists = f.some(ff => ff.flagKey === updated.flagKey);
        return exists ? f.map(ff => ff.flagKey === updated.flagKey ? updated : ff) : [...f, updated];
      });
      return updated;
    } catch (e) {
      alert('Update failed: ' + e.message);
    } finally { setSaving(null); }
  }

  async function toggleFlag(key, current) {
    await upsertFlag({ key, enabled: !current });
  }

  async function createFlag(e) {
    e.preventDefault();
    if (!newFlag.key.trim()) return;
    await upsertFlag({ key: newFlag.key.trim(), enabled: newFlag.enabled, note: newFlag.note || null });
    setNewFlag({ key: '', note: '', enabled: true });
    setCreating(false);
  }

  function startEditNote(flag) {
    setEditingNoteKey(flag.flagKey);
    setNoteDraft(flag.note || '');
  }
  async function saveNote(flag) {
    await upsertFlag({ key: flag.flagKey, enabled: flag.enabled, note: noteDraft });
    setEditingNoteKey(null);
  }

  function overrideBadge(flagKey) {
    const overridden = overrideKeys.includes(flagKey);
    if (!overridden) return null;
    return <span className="ml-2 text-[10px] px-1.5 py-0.5 rounded bg-amber-500/15 text-amber-400 border border-amber-500/30">overridden</span>;
  }

  function resetOverrides() {
    resetAllFeatureOverrides();
    refreshOverrideKeys();
  }

  return (
    <div className="min-h-screen bg-background">
      <HeaderNavigation />
      <main className="max-w-5xl mx-auto pt-28 px-6 pb-24">
        <div className="flex items-start justify-between gap-4 mb-6">
          <div>
            <h1 className="text-2xl font-semibold">Feature Flags</h1>
            <p className="text-text-secondary/80 text-sm mt-1">Runtimefetch flags with local overrides via ?ff=flag:on,other:off. Overrides persist locally.</p>
          </div>
          <div className="flex items-center gap-3">
            {overrideKeys.length > 0 && (
              <button onClick={resetOverrides} className="text-xs px-3 py-1.5 rounded bg-amber-500/10 hover:bg-amber-500/20 border border-amber-500/30 text-amber-400">Reset Overrides ({overrideKeys.length})</button>
            )}
            {!creating && (
              <button onClick={() => setCreating(true)} className="text-xs px-3 py-1.5 rounded bg-primary/10 hover:bg-primary/20 border border-primary/40 text-primary">New Flag</button>
            )}
          </div>
        </div>
        {!adminGateEnabled && <p className="text-text-secondary">Access disabled.</p>}
        {adminGateEnabled && (
          <>
            {creating && (
              <form onSubmit={createFlag} className="mb-6 p-4 border border-border-accent/30 rounded-lg bg-surface space-y-3">
                <div className="flex gap-4 flex-wrap">
                  <div className="flex-1 min-w-[160px]">
                    <label className="block text-[11px] uppercase tracking-wide text-text-secondary mb-1">Key</label>
                    <input value={newFlag.key} onChange={e => setNewFlag(f => ({ ...f, key: e.target.value }))} className="w-full px-2 py-1.5 rounded border border-border-accent/40 bg-background" placeholder="e.g. search_snippets" required />
                  </div>
                  <div className="w-32">
                    <label className="block text-[11px] uppercase tracking-wide text-text-secondary mb-1">Default</label>
                    <select value={newFlag.enabled ? 'on' : 'off'} onChange={e => setNewFlag(f => ({ ...f, enabled: e.target.value === 'on' }))} className="w-full px-2 py-1.5 rounded border border-border-accent/40 bg-background">
                      <option value="on">On</option>
                      <option value="off">Off</option>
                    </select>
                  </div>
                  <div className="flex-1 min-w-[200px]">
                    <label className="block text-[11px] uppercase tracking-wide text-text-secondary mb-1">Note</label>
                    <input value={newFlag.note} onChange={e => setNewFlag(f => ({ ...f, note: e.target.value }))} className="w-full px-2 py-1.5 rounded border border-border-accent/40 bg-background" placeholder="Purpose / context" />
                  </div>
                </div>
                <div className="flex gap-3 text-xs">
                  <button type="submit" disabled={!newFlag.key.trim()} className="px-3 py-1.5 rounded bg-primary/10 hover:bg-primary/20 border border-primary/40 text-primary disabled:opacity-50">Create</button>
                  <button type="button" onClick={() => { setCreating(false); setNewFlag({ key: '', note: '', enabled: true }); }} className="px-3 py-1.5 rounded border border-border-accent/40 hover:bg-border-accent/10">Cancel</button>
                </div>
              </form>
            )}
            {loading && <p className="text-sm text-text-secondary">Loading</p>}
            {error && <p className="text-error text-sm">{error}</p>}
            <div className="overflow-x-auto border border-border-accent/20 rounded-lg shadow-sm bg-surface">
              <table className="w-full text-sm">
                <thead className="bg-border-accent/10 text-text-secondary">
                  <tr>
                    <th className="text-left px-4 py-2 font-medium">Flag</th>
                    <th className="text-left px-4 py-2 font-medium">Enabled</th>
                    <th className="text-left px-4 py-2 font-medium">Note</th>
                    <th className="px-4 py-2" />
                  </tr>
                </thead>
                <tbody>
                  {flags.map(f => (
                    <tr key={f.flagKey} className="border-t border-border-accent/10">
                      <td className="px-4 py-2 font-mono text-xs flex items-center">{f.flagKey}{overrideBadge(f.flagKey)}</td>
                      <td className="px-4 py-2">
                        <button
                          disabled={saving === f.flagKey}
                          onClick={() => toggleFlag(f.flagKey, f.enabled)}
                          className={`text-[10px] px-2 py-1 rounded border transition-colors ${f.enabled ? 'bg-green-500/10 text-green-400 border-green-500/30 hover:bg-green-500/20' : 'bg-border-accent/20 text-text-secondary border-border-accent/40 hover:bg-border-accent/30'} disabled:opacity-50`}
                        >
                          {saving === f.flagKey ? '' : (f.enabled ? 'On' : 'Off')}
                        </button>
                      </td>
                      <td className="px-4 py-2 text-text-secondary/80 max-w-[320px]">
                        {editingNoteKey === f.flagKey ? (
                          <div className="flex items-center gap-2">
                            <input value={noteDraft} onChange={e => setNoteDraft(e.target.value)} className="flex-1 px-2 py-1 rounded border border-border-accent/40 bg-background text-xs" />
                            <button onClick={() => saveNote(f)} className="text-[10px] px-2 py-1 rounded bg-primary/10 text-primary border border-primary/30">Save</button>
                            <button onClick={() => setEditingNoteKey(null)} className="text-[10px] px-2 py-1 rounded border border-border-accent/40">Cancel</button>
                          </div>
                        ) : (
                          <div className="flex items-center justify-between gap-2">
                            <span title={f.note || ''} className="truncate text-xs">{f.note || ''}</span>
                            <button onClick={() => startEditNote(f)} className="text-[10px] px-2 py-0.5 rounded border border-border-accent/30 hover:bg-border-accent/10">Edit</button>
                          </div>
                        )}
                      </td>
                      <td className="px-4 py-2 text-right" />
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <p className="mt-4 text-[11px] text-text-secondary/70">Changes propagate to clients within ~30s (stale-while-revalidate loop & focus re-fetch). URL overrides via <code>?ff=myFlag:on,otherFlag:off</code>.</p>
          </>
        )}
      </main>
    </div>
  );
}

import React, { useEffect, useState } from 'react';
import { useAuth } from '../../contexts/AuthContext.jsx';

export default function MediaLibrary() {
  const { user } = useAuth();
  const [assets, setAssets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [tag, setTag] = useState('');
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [alt, setAlt] = useState('');
  const pageSize = 24;

  useEffect(() => {
    fetchAssets(); // eslint-disable-next-line
  }, [page, tag]);

  async function fetchAssets() {
    setLoading(true); setError(null);
    try {
      const params = new URLSearchParams({ limit: pageSize, offset: (page - 1) * pageSize });
      if (tag) params.set('tag', tag);
      const res = await fetch(`/api/media?${params.toString()}`);
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Failed');
      setAssets(data.items || []);
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  }

  async function handleUpload(e) {
    e.preventDefault();
    if (!file) return;
    setUploading(true);
    try {
      const form = new FormData();
      form.append('file', file);
      if (alt) form.append('alt', alt);
      if (tag) form.append('tags', tag);
      const res = await fetch('/api/media/upload', { method: 'POST', body: form });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Upload failed');
      setFile(null); setAlt('');
      fetchAssets();
    } catch (e) {
      setError(e.message);
    } finally {
      setUploading(false);
    }
  }

  if (!user?.is_admin) {
    return <div className="p-6 text-red-600">Admin access required.</div>;
  }

  return (
    <div className="p-6">
      <h1 className="text-2xl font-semibold mb-4">Media Library</h1>
      <form onSubmit={handleUpload} className="mb-6 flex flex-col gap-2 md:flex-row md:items-end">
        <div>
          <label className="block text-sm font-medium">File</label>
          <input type="file" accept="image/*" onChange={e => setFile(e.target.files?.[0] || null)} />
        </div>
        <div>
          <label className="block text-sm font-medium">Alt Text</label>
          <input className="border rounded px-2 py-1" value={alt} onChange={e => setAlt(e.target.value)} />
        </div>
        <div>
          <label className="block text-sm font-medium">Tag</label>
            <input className="border rounded px-2 py-1" value={tag} onChange={e => { setTag(e.target.value); setPage(1); }} />
        </div>
        <button disabled={uploading || !file} className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-50" type="submit">
          {uploading ? 'Uploading...' : 'Upload'}
        </button>
      </form>
      {error && <div className="text-red-600 mb-4">{error}</div>}
      {loading ? <div>Loading...</div> : (
        <div className="grid gap-4 grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6">
          {assets.map(a => (
            <figure key={a.id} className="border rounded overflow-hidden bg-white shadow-sm">
              <div className="aspect-square bg-gray-100 flex items-center justify-center overflow-hidden">
                <img src={a.url} alt={a.altText || ''} className="object-cover w-full h-full" loading="lazy" />
              </div>
              <figcaption className="p-2 text-xs break-words">
                <div className="font-medium truncate" title={a.url}>{a.url.split('/').pop()}</div>
                <div className="text-gray-500">{a.width}x{a.height}</div>
                {a.tags?.length ? <div className="mt-1 flex flex-wrap gap-1">{a.tags.map(t => <span key={t} className="bg-gray-200 px-1 rounded">{t}</span>)}</div> : null}
              </figcaption>
            </figure>
          ))}
        </div>
      )}
      <div className="mt-6 flex gap-2">
        <button disabled={page === 1} onClick={() => setPage(p => Math.max(1, p - 1))} className="px-3 py-1 border rounded disabled:opacity-50">Prev</button>
        <span className="px-2 py-1">Page {page}</span>
        <button onClick={() => setPage(p => p + 1)} className="px-3 py-1 border rounded">Next</button>
      </div>
    </div>
  );
}

