          <div className="text-xs text-text-secondary font-caption mt-1">Required by AdSense. Copy from your AdSense account.</div>
        </div>
        <div>
          <label className="block text-sm font-medium text-text-primary mb-2">Ad Slot ID (optional)</label>
          <Input
            type="text"
            placeholder="1234567890"
            value={localSettings.ads?.ad_slot || ''}
            onChange={(e) => handleAdsSettingChange('ad_slot', e.target.value)}
          />
          <div className="text-xs text-text-secondary font-caption mt-1">If empty, only Auto Ads are used (if enabled).</div>
        </div>
      </div>
      )}

      {localSettings.ads?.provider === 'yllix' && (
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-text-primary mb-2">Yllix Publisher ID</label>
            <Input
              type="text"
              placeholder="Your Yllix publisher id"
              value={localSettings.ads?.yllix_publisher_id || ''}
              onChange={(e) => handleAdsSettingChange('yllix_publisher_id', e.target.value)}
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-text-primary mb-2">Yllix Ad Unit Code</label>
            <textarea
              rows={4}
              className="w-full px-4 py-3 bg-surface border border-border-accent/20 rounded-lg text-text-primary placeholder-text-secondary focus:outline-none focus:ring-2 focus:ring-primary resize-none"
              placeholder="Paste the ad unit script snippet provided by Yllix"
              value={localSettings.ads?.yllix_unit_code || ''}
              onChange={(e) => handleAdsSettingChange('yllix_unit_code', e.target.value)}
            />
            <div className="text-xs text-text-secondary font-caption mt-1">We will inject this safely into the blog grid at your interval.</div>
          </div>
        </div>
      )}

      {localSettings.ads?.provider === 'custom' && (
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-text-primary mb-2">Custom Script URL</label>
            <Input
              type="url"
              placeholder="https://cdn.example.com/ads.js"
              value={localSettings.ads?.custom_script_url || ''}
              onChange={(e) => handleAdsSettingChange('custom_script_url', e.target.value)}
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-text-primary mb-2">Custom Ad HTML</label>
            <textarea
              rows={4}
              className="w-full px-4 py-3 bg-surface border border-border-accent/20 rounded-lg text-text-primary placeholder-text-secondary focus:outline-none focus:ring-2 focus:ring-primary resize-none"
              placeholder="<div class='your-ad'></div>"
              value={localSettings.ads?.custom_ad_html || ''}
              onChange={(e) => handleAdsSettingChange('custom_ad_html', e.target.value)}
            />
            <div className="text-xs text-text-secondary font-caption mt-1">Custom HTML placed as ad content. Use responsibly.</div>
          </div>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="flex items-center gap-2">
          <input id="auto-ads" type="checkbox" className="w-4 h-4" checked={!!localSettings.ads?.auto_ads} onChange={(e) => handleAdsSettingChange('auto_ads', e.target.checked)} />
          <label htmlFor="auto-ads" className="text-sm text-text-secondary">Enable Auto Ads</label>
        </div>
        <div>
          <label className="block text-sm font-medium text-text-primary mb-2">Grid Ad Interval</label>
          <Input
            type="number"
            min="3"
            step="1"
            value={localSettings.ads?.grid_interval ?? 6}
            onChange={(e) => handleAdsSettingChange('grid_interval', Math.max(1, Number(e.target.value) || 6))}
          />
          <div className="text-xs text-text-secondary font-caption mt-1">Insert an ad after this many posts (requires Ad Slot ID).</div>
        </div>
      </div>

      {/* Header Code Injection */}
      <div className="space-y-4">
        <h4 className="text-lg font-heading font-semibold text-text-primary flex items-center gap-2">
          <Icon name="Code" size={20} className="text-primary" />
          Code Injection
        </h4>
        <div>
          <label className="block text-sm font-medium text-text-primary mb-2">Header Code (HTML/JS)</label>
          <textarea
            className="w-full px-4 py-3 bg-surface border border-border-accent/20 rounded-lg text-text-primary font-mono text-sm resize-y"
            rows={6}
            placeholder="<!-- Analytics, ads, or custom scripts -->"
            value={localSettings.ads?.header_code || ''}
            onChange={(e) => handleAdsSettingChange('header_code', e.target.value)}
          />
          <div className="text-xs text-text-secondary font-caption mt-1">
            Code injected in the &lt;head&gt; tag. Use for analytics, ads, or custom scripts.
          </div>
        </div>
        <div>
          <label className="block text-sm font-medium text-text-primary mb-2">Footer Code (HTML/JS)</label>
          <textarea
            className="w-full px-4 py-3 bg-surface border border-border-accent/20 rounded-lg text-text-primary font-mono text-sm resize-y"
            rows={4}
            placeholder="<!-- Footer scripts -->"
            value={localSettings.ads?.footer_code || ''}
            onChange={(e) => handleAdsSettingChange('footer_code', e.target.value)}
          />
          <div className="text-xs text-text-secondary font-caption mt-1">
            Code injected before the closing &lt;/body&gt; tag.
          </div>
        </div>
      </div>
    </div>
  );

  const renderAdvancedSettings = () => (
    <div className="space-y-6">
      <div className="flex items-center justify-between p-4 bg-warning/5 rounded-lg border border-warning/20">
        <div className="flex items-center space-x-3">
          <Icon name="AlertTriangle" size={20} className="text-warning" />
          <div>
            <div className="font-medium text-text-primary">Maintenance Mode</div>
            <div className="text-sm text-text-secondary font-caption">Temporarily disable public access</div>
          </div>
        </div>
        <label className="relative inline-flex items-center cursor-pointer">
          <input
            type="checkbox"
            checked={localSettings.maintenanceMode}
            onChange={(e) => handleSettingChange('maintenanceMode', e.target.checked)}
            className="sr-only peer"
          />
          <div className="w-11 h-6 bg-surface peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-warning/20 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-warning"></div>
        </label>
      </div>

      <div>
        <label className="block text-sm font-medium text-text-primary mb-2">Custom CSS</label>
        <textarea
          value={localSettings.customCSS}
          onChange={(e) => handleSettingChange('customCSS', e.target.value)}
          rows={8}
          className="w-full px-4 py-3 bg-surface border border-border-accent/20 rounded-lg text-text-primary placeholder-text-secondary focus:outline-none focus:ring-2 focus:ring-primary resize-none font-code text-sm"
          placeholder="/* Add your custom CSS here */"
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-text-primary mb-2">Custom JavaScript</label>
        <textarea
          value={localSettings.customJS}
          onChange={(e) => handleSettingChange('customJS', e.target.value)}
          rows={8}
          className="w-full px-4 py-3 bg-surface border border-border-accent/20 rounded-lg text-text-primary placeholder-text-secondary focus:outline-none focus:ring-2 focus:ring-primary resize-none font-code text-sm"
          placeholder="// Add your custom JavaScript here"
        />
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-heading font-bold text-primary">Site Settings</h1>
          <p className="text-text-secondary font-caption mt-2">
            Configure your portfolio website settings and preferences
          </p>
        </div>
        {hasChanges && (
          <div className="flex items-center space-x-2">
            <Button
              variant="outline"
              size="sm"
              onClick={handleReset}
            >
              Reset
            </Button>
            <Button
              variant="primary"
              size="sm"
              onClick={handleSave}
              iconName="Save"
              iconPosition="left"
            >
              Save Changes
            </Button>
          </div>
        )}
      </div>

      {/* Tabs */}
      <div className="bg-surface rounded-lg border border-border-accent/20 overflow-hidden">
        <div className="flex overflow-x-auto">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center space-x-2 px-6 py-4 text-sm font-medium whitespace-nowrap transition-all duration-fast ${
                activeTab === tab.id
                  ? 'text-primary bg-primary/10 border-b-2 border-primary' :'text-text-secondary hover:text-primary hover:bg-primary/5'
              }`}
            >
              <Icon name={tab.icon} size={16} />
              <span>{tab.label}</span>
            </button>
          ))}
        </div>

        {/* Tab Content */}
        <div className="p-6">
          {activeTab === 'general' && renderGeneralSettings()}
          {activeTab === 'appearance' && renderAppearanceSettings()}
          {activeTab === 'content' && renderContentSettings()}
          {activeTab === 'tech' && renderTechStackSettings()}
          {activeTab === 'social' && renderSocialSettings()}
          {activeTab === 'seo' && renderSEOSettings()}
          {activeTab === 'monetization' && renderMonetizationSettings()}
          {activeTab === 'advanced' && renderAdvancedSettings()}
        </div>
      </div>

      {/* Save Notice */}
      {hasChanges && (
        <div className="fixed bottom-6 right-6 bg-primary text-background px-6 py-3 rounded-lg shadow-glow-primary">
          <div className="flex items-center space-x-2">
            <Icon name="AlertCircle" size={16} />
            <span className="text-sm font-medium">You have unsaved changes</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default SiteSettings;

import React, { useState } from 'react';
import { validateSlide, isValidUrl } from '../../../utils/validation';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';
import Image from '../../../components/AppImage';
import FileUpload from '../../../components/ui/FileUpload';

const SlidesManagement = ({ slides, onSlideUpdate, onSlideDelete, onSlideCreate, onSlideReorder }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSlides, setSelectedSlides] = useState([]);
  const [editingSlide, setEditingSlide] = useState(null);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [draggedSlide, setDraggedSlide] = useState(null);
  const [createForm, setCreateForm] = useState({
    title: '',
    subtitle: '',
    backgroundImage: '',
    ctaText: '',
    ctaLink: '',
    duration: 5,
    status: 'active',
    order: (slides?.length || 0) + 1,
  });
  const [editForm, setEditForm] = useState({});
  const [formErrors, setFormErrors] = useState({});

  const Modal = ({ title, onClose, onSubmit, submitText = 'Save', children }) => (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-background/80 backdrop-blur-sm">
      <div className="bg-surface border border-border-accent/20 rounded-lg shadow-glow-primary w-full max-w-2xl">
        <div className="px-6 py-4 border-b border-border-accent/20 flex items-center justify-between">
          <h3 className="text-lg font-heading font-semibold text-text-primary">{title}</h3>
          <button onClick={onClose} className="text-text-secondary hover:text-primary"></button>
        </div>
        <form onSubmit={onSubmit} className="p-6 space-y-4">
          {children}
          <div className="flex items-center justify-end space-x-2 pt-2">
            <Button type="button" variant="outline" onClick={onClose}>Cancel</Button>
            <Button type="submit" variant="primary">{submitText}</Button>
          </div>
        </form>
      </div>
    </div>
  );

  const openEdit = (slide) => {
    setEditingSlide(slide);
    setEditForm({
      title: slide.title || '',
      subtitle: slide.subtitle || '',
      backgroundImage: slide.backgroundImage || '',
      ctaText: slide.ctaText || '',
      ctaLink: slide.ctaLink || '',
      duration: slide.duration || 5,
      status: slide.status || 'active',
      order: slide.order || 1,
    });
  };

  const submitCreate = (e) => {
    e.preventDefault();
    const errs = validateSlide(createForm);
    setFormErrors(errs);
    if (Object.keys(errs).length) return;
    onSlideCreate({ ...createForm });
    setShowCreateModal(false);
    setCreateForm({ title: '', subtitle: '', backgroundImage: '', ctaText: '', ctaLink: '', duration: 5, status: 'active', order: (slides?.length || 0) + 1 });
  };

  const submitEdit = (e) => {
    e.preventDefault();
    if (!editingSlide) return;
    const errs = validateSlide(editForm);
    setFormErrors(errs);
    if (Object.keys(errs).length) return;
    onSlideUpdate(editingSlide.id, { ...editForm });
    setEditingSlide(null);
  };

  const filteredSlides = slides
    .filter(slide => 
      slide.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      slide.subtitle.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => a.order - b.order);

  const handleSelectSlide = (slideId) => {
    setSelectedSlides(prev => 
      prev.includes(slideId) 
        ? prev.filter(id => id !== slideId)
        : [...prev, slideId]
    );
  };

  const handleSelectAll = () => {
    setSelectedSlides(
      selectedSlides.length === filteredSlides.length 
        ? [] 
        : filteredSlides.map(s => s.id)
    );
  };

  const handleBulkDelete = () => {
    if (window.confirm(`Delete ${selectedSlides.length} selected slides?`)) {
      selectedSlides.forEach(id => onSlideDelete(id));
      setSelectedSlides([]);
    }
  };

  const handleDragStart = (e, slide) => {
    setDraggedSlide(slide);
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDrop = (e, targetSlide) => {
    e.preventDefault();
    if (draggedSlide && draggedSlide.id !== targetSlide.id) {
      onSlideReorder(draggedSlide.id, targetSlide.order);
    }
    setDraggedSlide(null);
  };

  const SlideCard = ({ slide }) => (
    <div
      draggable
      onDragStart={(e) => handleDragStart(e, slide)}
      onDragOver={handleDragOver}
      onDrop={(e) => handleDrop(e, slide)}
      className={`bg-surface rounded-lg border border-border-accent/20 overflow-hidden hover:shadow-glow-primary transition-all duration-fast cursor-move ${
        draggedSlide?.id === slide.id ? 'opacity-50' : ''
      }`}
    >
      {/* Slide Preview */}
      <div className="relative h-48 bg-gradient-to-br from-primary/20 to-secondary/20">
        <Image 
          src={slide.backgroundImage} 
          alt={slide.title}
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-background/80 to-transparent"></div>
        <div className="absolute top-4 left-4">
          <input
            type="checkbox"
            checked={selectedSlides.includes(slide.id)}
            onChange={() => handleSelectSlide(slide.id)}
            className="w-4 h-4 text-primary bg-surface border-border-accent rounded focus:ring-primary focus:ring-2"
          />
        </div>
        <div className="absolute top-4 right-4 flex items-center space-x-2">
          <span className={`px-2 py-1 rounded-full text-xs font-medium ${
            slide.status === 'active' ? 'bg-success/10 text-success' : 'bg-warning/10 text-warning'
          }`}>
            {slide.status}
          </span>
          <div className="bg-background/80 rounded-full px-2 py-1 text-xs font-caption text-text-primary">
            #{slide.order}
          </div>
        </div>
        <div className="absolute bottom-4 left-4 right-4">
          <h3 className="text-lg font-heading font-bold text-text-primary mb-1 truncate">
            {slide.title}
          </h3>
          <p className="text-sm text-text-secondary font-caption truncate">
            {slide.subtitle}
          </p>
        </div>
      </div>

      {/* Slide Info */}
      <div className="p-4">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-2">
            <Icon name="Clock" size={14} className="text-text-secondary" />
            <span className="text-xs text-text-secondary font-caption">
              Duration: {slide.duration}s
            </span>
          </div>
          <div className="flex items-center space-x-2">
            <Icon name="Eye" size={14} className="text-text-secondary" />
            <span className="text-xs text-text-secondary font-caption">
              {slide.views} views
            </span>
          </div>
        </div>

        {/* CTA Button Preview */}
        {slide.ctaText && (
          <div className="mb-3">
            <div className="text-xs text-text-secondary font-caption mb-1">Call to Action:</div>
            <div className="inline-flex items-center px-3 py-1 bg-primary/10 text-primary rounded-lg text-sm">
              {slide.ctaText}
              <Icon name="ArrowRight" size={14} className="ml-1" />
            </div>
          </div>
        )}

        {/* Actions */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => openEdit(slide)}
              iconName="Edit"
              className="p-2"
              title="Edit slide"
            />
            <Button
              variant="ghost"
              size="sm"
              onClick={() => {
                if (window.confirm('Delete this slide?')) {
                  onSlideDelete(slide.id);
                }
              }}
              iconName="Trash2"
              className="p-2 text-error hover:text-error"
              title="Delete slide"
            />
          </div>
          <div className="flex items-center space-x-1">
            <Icon name="GripVertical" size={16} className="text-text-secondary" />
            <span className="text-xs text-text-secondary font-caption">Drag to reorder</span>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-heading font-bold text-primary">Hero Slides Management</h1>
          <p className="text-text-secondary font-caption mt-2">
            Manage carousel slides and hero section content
          </p>
        </div>
        <Button
          variant="primary"
          onClick={() => setShowCreateModal(true)}
          iconName="ImagePlus"
          iconPosition="left"
        >
          New Slide
        </Button>
      </div>

      {/* Controls */}
      <div className="bg-surface rounded-lg p-6 border border-border-accent/20">
        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
          <div className="flex-1 max-w-md">
            <Input
              type="search"
              placeholder="Search slides..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full"
            />
          </div>
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2 text-sm text-text-secondary font-caption">
              <Icon name="Info" size={16} />
              <span>Drag slides to reorder carousel sequence</span>
            </div>
            {selectedSlides.length > 0 && (
              <Button
                variant="danger"
                size="sm"
                onClick={handleBulkDelete}
                iconName="Trash2"
                iconPosition="left"
              >
                Delete ({selectedSlides.length})
              </Button>
            )}
          </div>
        </div>
      </div>

      {/* Slides Grid */}
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              checked={selectedSlides.length === filteredSlides.length && filteredSlides.length > 0}
              onChange={handleSelectAll}
              className="w-4 h-4 text-primary bg-surface border-border-accent rounded focus:ring-primary focus:ring-2"
            />
            <span className="text-sm text-text-secondary font-caption">
              Select all ({filteredSlides.length} slides)
            </span>
          </div>
          <div className="text-sm text-text-secondary font-caption">
            {filteredSlides.filter(s => s.status === 'active').length} active slides
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredSlides.map((slide) => (
            <SlideCard key={slide.id} slide={slide} />
          ))}
        </div>

        {filteredSlides.length === 0 && (
          <div className="text-center py-12">
            <Icon name="Image" size={48} className="text-text-secondary/50 mx-auto mb-4" />
            <div className="text-text-secondary font-caption">
              {searchTerm ? 'No slides match your search' : 'No slides found'}
            </div>
          </div>
        )}
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="bg-surface rounded-lg p-4 border border-border-accent/20">
          <div className="text-2xl font-heading font-bold text-primary">{slides.length}</div>
          <div className="text-sm text-text-secondary font-caption">Total Slides</div>
        </div>
        <div className="bg-surface rounded-lg p-4 border border-border-accent/20">
          <div className="text-2xl font-heading font-bold text-success">
            {slides.filter(s => s.status === 'active').length}
          </div>
          <div className="text-sm text-text-secondary font-caption">Active</div>
        </div>
        <div className="bg-surface rounded-lg p-4 border border-border-accent/20">
          <div className="text-2xl font-heading font-bold text-warning">
            {slides.filter(s => s.status === 'inactive').length}
          </div>
          <div className="text-sm text-text-secondary font-caption">Inactive</div>
        </div>
        <div className="bg-surface rounded-lg p-4 border border-border-accent/20">
          <div className="text-2xl font-heading font-bold text-text-primary">
            {slides.reduce((sum, slide) => sum + slide.views, 0).toLocaleString()}
          </div>
          <div className="text-sm text-text-secondary font-caption">Total Views</div>
        </div>
      </div>

      {/* Carousel Preview */}
      <div className="bg-surface rounded-lg p-6 border border-border-accent/20">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-heading font-semibold text-text-primary">Carousel Preview</h3>
          <Button
            variant="outline"
            size="sm"
            iconName="Play"
            iconPosition="left"
          >
            Preview Carousel
          </Button>
        </div>
        <div className="h-32 bg-gradient-to-r from-primary/10 to-secondary/10 rounded-lg flex items-center justify-center">
          <div className="text-center">
            <Icon name="Play" size={32} className="text-text-secondary/50 mx-auto mb-2" />
            <div className="text-sm text-text-secondary font-caption">
              Click "Preview Carousel" to see slides in action
            </div>
          </div>
        </div>
      </div>

      {/* Create Modal */}
      {showCreateModal && (
        <Modal title="Create Slide" onClose={() => setShowCreateModal(false)} onSubmit={submitCreate} submitText="Create">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm text-text-secondary mb-1">Title</label>
              <Input value={createForm.title} onChange={(e) => setCreateForm({ ...createForm, title: e.target.value })} required />
              {formErrors.title && <p className="mt-1 text-xs text-error">{formErrors.title}</p>}
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">Subtitle</label>
              <Input value={createForm.subtitle} onChange={(e) => setCreateForm({ ...createForm, subtitle: e.target.value })} />
            </div>
            <div className="md:col-span-2">
              <FileUpload
                label="Background Image"
                value={createForm.backgroundImage}
                onChange={(url) => setCreateForm({ ...createForm, backgroundImage: url })}
                bucket="media"
                pathPrefix="slides"
                accept="image"
                helperText="Upload a background image for this slide."
              />
              {formErrors.backgroundImage && <p className="mt-1 text-xs text-error">{formErrors.backgroundImage}</p>}
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">CTA Text</label>
              <Input value={createForm.ctaText} onChange={(e) => setCreateForm({ ...createForm, ctaText: e.target.value })} />
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">CTA Link</label>
              <Input value={createForm.ctaLink} onChange={(e) => setCreateForm({ ...createForm, ctaLink: e.target.value })} />
              {formErrors.ctaLink && <p className="mt-1 text-xs text-error">{formErrors.ctaLink}</p>}
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">Duration (seconds)</label>
              <Input type="number" min="1" value={createForm.duration} onChange={(e) => setCreateForm({ ...createForm, duration: Number(e.target.value) || 5 })} />
              {formErrors.duration && <p className="mt-1 text-xs text-error">{formErrors.duration}</p>}
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">Status</label>
              <select className="w-full bg-surface border border-border-accent/20 rounded-lg px-3 py-2 text-sm" value={createForm.status} onChange={(e) => setCreateForm({ ...createForm, status: e.target.value })}>
                <option value="active">Active</option>
                <option value="inactive">Inactive</option>
              </select>
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">Order</label>
              <Input type="number" min="1" value={createForm.order} onChange={(e) => setCreateForm({ ...createForm, order: Number(e.target.value) || 1 })} />
              {formErrors.order && <p className="mt-1 text-xs text-error">{formErrors.order}</p>}
            </div>
          </div>
        </Modal>
      )}

      {/* Edit Modal */}
      {editingSlide && (
        <Modal title="Edit Slide" onClose={() => setEditingSlide(null)} onSubmit={submitEdit} submitText="Update">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm text-text-secondary mb-1">Title</label>
              <Input value={editForm.title} onChange={(e) => setEditForm({ ...editForm, title: e.target.value })} required />
              {formErrors.title && <p className="mt-1 text-xs text-error">{formErrors.title}</p>}
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">Subtitle</label>
              <Input value={editForm.subtitle} onChange={(e) => setEditForm({ ...editForm, subtitle: e.target.value })} />
            </div>
            <div className="md:col-span-2">
              <FileUpload
                label="Background Image"
                value={editForm.backgroundImage}
                onChange={(url) => setEditForm({ ...editForm, backgroundImage: url })}
                bucket="media"
                pathPrefix="slides"
                accept="image"
                helperText="Upload a background image for this slide."
              />
              {formErrors.backgroundImage && <p className="mt-1 text-xs text-error">{formErrors.backgroundImage}</p>}
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">CTA Text</label>
              <Input value={editForm.ctaText} onChange={(e) => setEditForm({ ...editForm, ctaText: e.target.value })} />
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">CTA Link</label>
              <Input value={editForm.ctaLink} onChange={(e) => setEditForm({ ...editForm, ctaLink: e.target.value })} />
              {formErrors.ctaLink && <p className="mt-1 text-xs text-error">{formErrors.ctaLink}</p>}
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">Duration (seconds)</label>
              <Input type="number" min="1" value={editForm.duration} onChange={(e) => setEditForm({ ...editForm, duration: Number(e.target.value) || 5 })} />
              {formErrors.duration && <p className="mt-1 text-xs text-error">{formErrors.duration}</p>}
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">Status</label>
              <select className="w-full bg-surface border border-border-accent/20 rounded-lg px-3 py-2 text-sm" value={editForm.status} onChange={(e) => setEditForm({ ...editForm, status: e.target.value })}>
                <option value="active">Active</option>
                <option value="inactive">Inactive</option>
              </select>
            </div>
            <div>
              <label className="block text-sm text-text-secondary mb-1">Order</label>
              <Input type="number" min="1" value={editForm.order} onChange={(e) => setEditForm({ ...editForm, order: Number(e.target.value) || 1 })} />
              {formErrors.order && <p className="mt-1 text-xs text-error">{formErrors.order}</p>}
            </div>
          </div>
        </Modal>
      )}
    </div>
  );
};

export default SlidesManagement;

import { useEffect, useState, useCallback } from "react";
import projectService from "../../utils/projectService";
import blogService from "../../utils/blogService";
import slideService from "../../utils/slideService";
import settingsService from "../../utils/settingsService";
import publisherProgramService from "../../utils/publisherProgramService";
import useFeature from "../../hooks/useFeature";
import { useToast } from "../../contexts/ToastContext";

const AdminDashboardContentManagement = () => {
  const { show } = useToast();
  const [activeSection, setActiveSection] = useState("overview");
  const [isAuthenticated, setIsAuthenticated] = useState(true);
  const [projects, setProjects] = useState([]);
  const [blogPosts, setBlogPosts] = useState([]);
  const [slides, setSlides] = useState([]);
  const [moderationComments, setModerationComments] = useState([]);
  const [moderationLoading, setModerationLoading] = useState(false);
  const [settings, setSettings] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [blogEditorState, setBlogEditorState] = useState({
    open: false,
    mode: "create",
    post: null,
  });
  const publisherProgramEnabled = useFeature("publisher_program");
  const [publisherRequests, setPublisherRequests] = useState([]);
  const [publisherReqLoading, setPublisherReqLoading] = useState(false);
  const [publisherReqActionIds, setPublisherReqActionIds] = useState([]); // ids currently being processed

  const loadPublisherRequests = async () => {
    if (!publisherProgramEnabled) return;
    setPublisherReqLoading(true);
    const res = await publisherProgramService.listPending();
    if (res.success) setPublisherRequests(res.data);
    setPublisherReqLoading(false);
  };
  useEffect(() => {
    if (publisherProgramEnabled) loadPublisherRequests();
    // eslint-disable-next-line react-hooks/exhaustive-deps -- loadPublisherRequests stable enough
  }, [publisherProgramEnabled]);

  const handlePublisherDecision = async (user_id, action) => {
    const fn =
      action === "approve"
        ? publisherProgramService.approve
        : publisherProgramService.reject;
    setPublisherReqActionIds((ids) => [...ids, user_id]);
    // Optimistic removal
    setPublisherRequests((prev) => prev.filter((p) => p.id !== user_id));
    const res = await fn(user_id);
    if (res.success) {
      show(`User ${action}d`, { type: "success" });
    } else {
      show(res.error || "Action failed", { type: "error" });
      // Revert on failure: re-fetch list
      await loadPublisherRequests();
    }
    setPublisherReqActionIds((ids) => ids.filter((id) => id !== user_id));
  };

  // Helper mappers (UI shape -> DB shape)
  const toDbProject = (data = {}) => {
    const { image, date: _date, ...rest } = data || {}; // _date ignored
    return {
      ...rest,
      featured_image: image,
      // created_at is managed by DB; ignore date from UI
    };
  };

  const toDbPost = (data = {}) => {
    const {
      featuredImage,
      publishDate,
      readTime,
      slug,
      featured,
      metaTitle,
      metaDescription,
      publishedAt,
      ...rest
    } = data || {};
    return {
      ...rest,
      featured_image: featuredImage,
      published_at: publishedAt ?? publishDate,
      read_time: readTime,
      slug,
      featured,
      meta_title: metaTitle,
      meta_description: metaDescription,
    };
  };

  const toDbSlide = (data = {}) => {
    const {
      backgroundImage,
      ctaText,
      ctaLink,
      order,
      views: _views,
      status,
      ...rest
    } = data || {};
    return {
      ...rest,
      background_image: backgroundImage,
      cta_text: ctaText,
      cta_link: ctaLink,
      display_order: order,
      // Map UI status to DB content_status
      status:
        status === "active"
          ? "published"
          : status === "inactive"
            ? "draft"
            : status,
    };
  };

  // Mock data for dashboard
  const mockStats = {
    projects: projects.length,
    blogPosts: blogPosts.length,
    slides: slides.length,
    totalViews: 0,
  };

  const mockAnalytics = {
    visitorData: [
      { date: "2024-01-01", visitors: 120 },
      { date: "2024-01-02", visitors: 150 },
      { date: "2024-01-03", visitors: 180 },
      { date: "2024-01-04", visitors: 200 },
      { date: "2024-01-05", visitors: 170 },
      { date: "2024-01-06", visitors: 220 },
      { date: "2024-01-07", visitors: 250 },
    ],
    contentData: [
      { type: "Projects", views: 8500 },
      { type: "Blog", views: 12000 },
      { type: "About", views: 4200 },
    ],
    recentActivity: [
      {
        type: "create",
        action: 'Created new blog post "Advanced React Patterns"',
        timestamp: "2 hours ago",
      },
      {
        type: "update",
        action: 'Updated project "E-commerce Platform"',
        timestamp: "4 hours ago",
      },
      {
        type: "create",
        action: 'Added new hero slide "Innovation Showcase"',
        timestamp: "1 day ago",
      },
      {
        type: "update",
        action: "Modified site settings",
        timestamp: "2 days ago",
      },
      {
        type: "delete",
        action: 'Removed outdated project "Legacy App"',
        timestamp: "3 days ago",
      },
