      case "analytics":
        return <AnalyticsOverview />;
      case "projects":
        return (
          <ProjectsManagement
            projects={(projects || []).map((p) => ({
              id: p.id,
              title: p.title,
              description: p.description,
              image: p.featured_image || "/assets/images/no_image.png",
              technologies: p.technologies || [],
              status: p.status,
              date: p.created_at,
            }))}
            onProjectUpdate={handleProjectUpdate}
            onProjectDelete={handleProjectDelete}
            onProjectCreate={handleProjectCreate}
          />
        );
      case "blog":
        return (
          <div className="h-full flex flex-col">
            {!blogEditorState.open && (
              <BlogManagement
                blogPosts={normalizedPosts}
                onPostUpdate={handlePostUpdate}
                onPostDelete={handlePostDelete}
                onPostCreate={handlePostCreate}
                onOpenEditor={({ mode, post }) =>
                  setBlogEditorState({
                    open: true,
                    mode: mode || "create",
                    post: post || null,
                  })
                }
              />
            )}
            {blogEditorState.open && (
              <div className="flex-1 min-h-[60vh]">
                <BlogEditor
                  mode={blogEditorState.mode}
                  initialData={
                    blogEditorState.mode === "edit" && blogEditorState.post
                      ? blogEditorState.post
                      : {}
                  }
                  onCancel={() =>
                    setBlogEditorState({
                      open: false,
                      mode: "create",
                      post: null,
                    })
                  }
                  onSave={(data) =>
                    blogEditorState.mode === "edit" && blogEditorState.post
                      ? handlePostUpdate(blogEditorState.post.id, data)
                      : handlePostCreate(data)
                  }
                />
              </div>
            )}
          </div>
        );
      case "slides":
        return (
          <SlidesManagement
            slides={normalizedSlides}
            onSlideUpdate={handleSlideUpdate}
            onSlideDelete={handleSlideDelete}
            onSlideCreate={handleSlideCreate}
            onSlideReorder={handleSlideReorder}
          />
