const BlogCard = ({ post, onLike, onShare, onPostClick, isAdmin, onEdit, onDelete }) => {
  const formatDate = (dateString) => {
    if (!dateString) return 'Unknown date';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const handleLike = (e) => {
    e.preventDefault();
    e.stopPropagation();
    onLike?.(post.id);
  };

  const handleShare = (e) => {
    e.preventDefault();
    e.stopPropagation();
    onShare?.(post);
  };

  const handleEdit = (e) => {
    e.preventDefault();
    e.stopPropagation();
    onEdit?.(post);
  };

  const handleDelete = (e) => {
    e.preventDefault();
    e.stopPropagation();
    onDelete?.(post.id);
  };

  const handleCardClick = () => {
    onPostClick?.(post);
  };

  // Prepare display data with fallbacks
  const displayData = {
    title: post?.title || 'Untitled Post',
    excerpt: post?.excerpt || 'No excerpt available',
    category: post?.category || 'Uncategorized',
    tags: post?.tags || [],
    featuredImage: post?.featured_image || post?.featuredImage,
    publishDate: post?.published_at || post?.publishDate || post?.created_at,
    author: post?.author?.full_name || post?.author || 'Unknown Author',
    readTime: post?.read_time || 5,
