          </div>
          <div className="flex items-center space-x-1">
            <Icon name="Clock" size={14} />
            <span>{displayData.readTime} min read</span>
          </div>
        </div>

        {/* Tags */}
        <div className="flex flex-wrap gap-1 mb-4">
          {displayData.tags?.slice(0, 3).map((tag, index) => (
            <span
