const BlogModal = ({ isOpen, onClose, post, onSave, isAdmin }) => {
  const { user, userProfile } = useAuth();
  const [formData, setFormData] = useState({
    title: '',
    excerpt: '',
    content: '',
    category: '',
    tags: '',
    featured_image: '',
    status: 'draft',
    meta_title: '',
    meta_description: ''
  });

  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState({
    author_name: '',
    author_email: '',
    content: ''
  });
  const [replyTo, setReplyTo] = useState(null);
  const [replyData, setReplyData] = useState({
    author_name: '',
    author_email: '',
    content: ''
  });
  const [loading, setLoading] = useState(false);
  const [commentLoading, setCommentLoading] = useState(false);
  const [newsletterSignup, setNewsletterSignup] = useState(false);

  useEffect(() => {
    if (post) {
      setFormData({
        title: post.title || '',
        excerpt: post.excerpt || '',
        content: post.content || '',
        category: post.category || '',
        tags: Array.isArray(post.tags) ? post.tags.join(', ') : (post.tags || ''),
        featured_image: post.featured_image || post.featuredImage || '',
        status: post.status || 'draft',
        meta_title: post.meta_title || '',
        meta_description: post.meta_description || ''
      });

      // Load comments if viewing a published post
      if (post.id && post.status === 'published') {
        loadComments();
      } else {
        setComments(post.comments || []);
      }
    }
  }, [post]);

  const loadComments = async () => {
    if (!post?.id) return;

    try {
      setCommentLoading(true);
      const result = await commentService.getComments(post.id);

      if (result.success) {
        setComments(result.data || []);
      }
    } catch (error) {
      console.log('Failed to load comments:', error);
    } finally {
      setCommentLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSave = async () => {
    try {
      setLoading(true);

      const updatedPost = {
        ...post,
        ...formData,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        author_id: user?.id,
        author: userProfile?.full_name || 'Admin'
      };

      await onSave(updatedPost);
    } catch (error) {
      console.log('Failed to save post:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();

    if (!newComment.content.trim() || !newComment.author_name.trim()) {
      return;
    }

    try {
      setCommentLoading(true);

      const commentData = {
        blog_post_id: post.id,
        author_name: newComment.author_name,
        author_email: newComment.author_email,
        content: newComment.content,
        parent_comment_id: null
      };

      const result = await commentService.createComment(commentData);

      if (result.success) {
        // Add comment to local state
        setComments(prev => [result.data, ...prev]);

        // Reset form
        setNewComment({
          author_name: '',
          author_email: '',
          content: ''
        });

        // Handle newsletter signup
        if (newsletterSignup && newComment.author_email) {
          try {
            const result = await newsletterService.subscribe(newComment.author_email, newComment.author_name);
            if (result.success) {
              console.log('Successfully subscribed to newsletter:', newComment.author_email);
            } else {
              console.log('Newsletter signup error:', result.error);
            }
          } catch (error) {
            console.log('Newsletter signup failed:', error);
          }
        }
        setNewsletterSignup(false);
      }
    } catch (error) {
      console.log('Failed to add comment:', error);
    } finally {
      setCommentLoading(false);
    }
  };

  const handleReplySubmit = async (parentCommentId) => {
    if (!replyData.content.trim() || !replyData.author_name.trim()) {
      return;
    }

    try {
      setCommentLoading(true);

      const commentData = {
        blog_post_id: post.id,
        parent_comment_id: parentCommentId,
        author_name: replyData.author_name,
        author_email: replyData.author_email,
        content: replyData.content
      };

      const result = await commentService.createComment(commentData);

      if (result.success) {
        // Reload comments to get updated structure
        await loadComments();

        // Reset reply form
        setReplyData({
          author_name: '',
          author_email: '',
          content: ''
        });
        setReplyTo(null);
      }
    } catch (error) {
      console.log('Failed to add reply:', error);
    } finally {
      setCommentLoading(false);
    }
  };

  const handleCommentLike = async (commentId) => {
    try {
      // Get visitor IP for like tracking
      const visitorIp = await getVisitorIp();
      const userAgent = navigator.userAgent || '';

      const result = await commentService.toggleLike(commentId, visitorIp, userAgent);

      if (result.success) {
        // Update local comment state
        setComments(prev => 
          prev.map(comment => 
            comment.id === commentId 
              ? { 
                  ...comment, 
                  like_count: result.liked 
                    ? (comment.like_count || 0) + 1 
                    : Math.max((comment.like_count || 0) - 1, 0)
                }
              : comment
          )
        );
      }
    } catch (error) {
      console.log('Failed to toggle comment like:', error);
    }
  };

  // Get visitor IP helper function
  const getVisitorIp = async () => {
    try {
      const response = await fetch('https://api.ipify.org?format=json');
      const data = await response.json();
      return data.ip || 'unknown';
    } catch (error) {
      return `fallback-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Unknown date';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (!isOpen || !post) return null;

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
        {!isAdmin && (
          <Helmet>
            <title>{formData.meta_title || post.title}</title>
            <meta name="description" content={formData.meta_description || post.excerpt || ''} />
            <meta property="og:title" content={formData.meta_title || post.title} />
            <meta property="og:description" content={formData.meta_description || post.excerpt || ''} />
            <meta property="og:url" content={`${SITE_URL}/blog/${post.slug}`} />
            <link rel="canonical" href={`${SITE_URL}/blog/${post.slug}`} />
            <meta name="twitter:card" content="summary_large_image" />
            <meta name="twitter:site" content={TWITTER_HANDLE} />
            <meta name="twitter:creator" content={TWITTER_HANDLE} />
          </Helmet>
        )}
        {/* Backdrop */}
        <div 
          className="fixed inset-0 bg-background/80 backdrop-blur-sm transition-opacity"
          onClick={onClose}
        ></div>

        {/* Modal */}
        <div className="inline-block align-bottom bg-surface rounded-lg border border-border-accent/20 text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-4xl sm:w-full">
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-border-accent/20">
            <h2 className="font-heading font-bold text-xl text-text-primary">
              {isAdmin ? (post.id ? 'Edit Blog Post' : 'Create Blog Post') : post.title}
            </h2>
            <Button
              variant="ghost"
              size="sm"
              iconName="X"
              onClick={onClose}
              className="text-text-secondary hover:text-primary"
            />
          </div>

          {/* Content */}
          <div className="max-h-[80vh] overflow-y-auto">
            {isAdmin ? (
              /* Admin Edit Form */
              <div className="p-6 space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-text-primary mb-2">
                      Title *
                    </label>
                    <Input
                      type="text"
                      name="title"
                      value={formData.title}
                      onChange={handleInputChange}
                      placeholder="Enter post title"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-text-primary mb-2">
                      Category
                    </label>
                    <Input
                      type="text"
                      name="category"
                      value={formData.category}
                      onChange={handleInputChange}
                      placeholder="Enter category"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-text-primary mb-2">
                    Featured Image URL
                  </label>
                  <Input
                    type="url"
                    name="featured_image"
                    value={formData.featured_image}
                    onChange={handleInputChange}
                    placeholder="Enter image URL"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-text-primary mb-2">
                    Excerpt *
                  </label>
                  <textarea
                    name="excerpt"
                    value={formData.excerpt}
                    onChange={handleInputChange}
                    rows={3}
                    className="w-full px-3 py-2 bg-background border border-border-accent/40 rounded-lg text-text-primary placeholder-text-secondary focus:border-primary/60 focus:outline-none resize-none"
                    placeholder="Enter post excerpt"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-text-primary mb-2">
                    Content *
                  </label>
                  <textarea
                    name="content"
                    value={formData.content}
                    onChange={handleInputChange}
                    rows={12}
                    className="w-full px-3 py-2 bg-background border border-border-accent/40 rounded-lg text-text-primary placeholder-text-secondary focus:border-primary/60 focus:outline-none resize-none"
                    placeholder="Enter post content (supports markdown)"
                    required
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-text-primary mb-2">
                      Tags (comma-separated)
                    </label>
                    <Input
                      type="text"
                      name="tags"
                      value={formData.tags}
                      onChange={handleInputChange}
                      placeholder="react, javascript, tutorial"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-text-primary mb-2">
                      Status
                    </label>
                    <select
                      name="status"
                      value={formData.status}
                      onChange={handleInputChange}
                      className="w-full px-3 py-2 bg-background border border-border-accent/40 rounded-lg text-text-primary focus:border-primary/60 focus:outline-none"
                    >
                      <option value="draft">Draft</option>
                      <option value="published">Published</option>
                      <option value="archived">Archived</option>
                    </select>
                  </div>
                </div>

                <div className="flex justify-end space-x-3 pt-4 border-t border-border-accent/20">
                  <Button variant="ghost" onClick={onClose} disabled={loading}>
                    Cancel
                  </Button>
                  <Button 
                    variant="primary" 
                    onClick={handleSave}
                    disabled={loading || !formData.title.trim() || !formData.content.trim()}
                    iconName={loading ? "Loader2" : "Save"}
                    iconPosition="left"
                    className={loading ? "animate-spin" : ""}
                  >
                    {loading ? 'Saving...' : (post.id ? 'Update Post' : 'Create Post')}
                  </Button>
                </div>
              </div>
            ) : (
              /* Blog Post View */
              <div className="p-6">
                {/* Featured Image */}
                {formData.featured_image && (
                  <div className="mb-6">
                    <Image
                      src={formData.featured_image}
                      alt={formData.title}
                      className="w-full h-64 object-cover rounded-lg"
                    />
                  </div>
                )}

                {/* Post Meta */}
                <div className="flex items-center justify-between mb-6 text-sm text-text-secondary font-caption">
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-1">
                      <Icon name="Calendar" size={16} />
                      <span>{formatDate(post.published_at || post.created_at)}</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <Icon name="User" size={16} />
                      <span>{post.author?.full_name || post.author || 'Unknown Author'}</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <Icon name="Clock" size={16} />
                      <span>{post.read_time || 5} min read</span>
                    </div>
                  </div>
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-1">
                      <Icon name="Heart" size={16} />
                      <span>{post.like_count || 0}</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <Icon name="MessageCircle" size={16} />
                      <span>{comments.length}</span>
                    </div>
                  </div>
                </div>

                {/* Post Content */}
                <div className="prose prose-invert max-w-none mb-8">
                  <div className="text-text-primary leading-relaxed whitespace-pre-wrap">
                    {formData.content}
                  </div>
                </div>

                {/* Tags */}
                {formData.tags && (
                  <div className="flex flex-wrap gap-2 mb-8">
                    {formData.tags.split(',').map((tag, index) => (
                      <span
                        key={index}
                        className="px-3 py-1 bg-accent/10 text-accent text-sm rounded-full"
                      >
                        #{tag.trim()}
                      </span>
                    ))}
                  </div>
                )}

                {/* Source Attribution */}
                {post.source_url && (
                  <div className="mb-8 p-4 bg-background/30 border border-border-accent/20 rounded-lg">
                    <div className="flex items-center space-x-2 text-sm text-text-secondary">
                      <Icon name="ExternalLink" size={16} />
                      <span>Source:</span>
                      <a 
                        href={post.source_url} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-primary hover:text-secondary underline"
                      >
                        {new URL(post.source_url).hostname}
                      </a>
                    </div>
                  </div>
                )}

                {/* Comments Section - Only for published posts */}
                {post.status === 'published' && (
                  <div className="border-t border-border-accent/20 pt-8">
                    <h3 className="font-heading font-semibold text-lg text-text-primary mb-6">
                      Comments ({comments.length})
                    </h3>

                    {/* Add Comment Form */}
                    <form onSubmit={handleCommentSubmit} className="mb-8 p-4 bg-background/50 rounded-lg">
                      <h4 className="font-medium text-text-primary mb-4">Leave a Comment</h4>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                        <Input
                          type="text"
                          placeholder="Your name *"
                          value={newComment.author_name}
                          onChange={(e) => setNewComment(prev => ({ ...prev, author_name: e.target.value }))}
                          required
                        />
                        <Input
                          type="email"
                          placeholder="Your email"
                          value={newComment.author_email}
                          onChange={(e) => setNewComment(prev => ({ ...prev, author_email: e.target.value }))}
                        />
                      </div>

                      <textarea
                        value={newComment.content}
                        onChange={(e) => setNewComment(prev => ({ ...prev, content: e.target.value }))}
                        placeholder="Share your thoughts..."
                        rows={4}
                        className="w-full px-3 py-2 bg-background border border-border-accent/40 rounded-lg text-text-primary placeholder-text-secondary focus:border-primary/60 focus:outline-none resize-none mb-4"
                        required
                      />

                      {/* Newsletter Signup Checkbox */}
                      <div className="flex items-center space-x-2 mb-4">
                        <input
                          type="checkbox"
                          id="newsletter"
                          checked={newsletterSignup}
                          onChange={(e) => setNewsletterSignup(e.target.checked)}
                          className="rounded border-border-accent/40 text-primary focus:ring-primary"
                        />
                        <label htmlFor="newsletter" className="text-sm text-text-secondary">
                          Subscribe to our newsletter for updates
                        </label>
                      </div>

                      <div className="flex justify-end">
                        <Button
                          type="submit"
                          variant="primary"
                          disabled={commentLoading || !newComment.content.trim() || !newComment.author_name.trim()}
                          iconName={commentLoading ? "Loader2" : "Send"}
                          iconPosition="left"
                          className={commentLoading ? "animate-spin" : ""}
                        >
                          {commentLoading ? 'Posting...' : 'Post Comment'}
                        </Button>
                      </div>
                    </form>

                    {/* Comments List */}
                    <div className="space-y-6">
                      {commentLoading ? (
                        <div className="flex items-center justify-center py-8">
                          <Icon name="Loader2" size={24} className="animate-spin text-primary" />
                          <span className="ml-2 text-text-secondary">Loading comments...</span>
                        </div>
                      ) : comments.length > 0 ? (
                        comments.map((comment) => (
                          <div key={comment.id} className="bg-background/50 rounded-lg p-4">
                            <div className="flex items-center justify-between mb-2">
                              <div className="flex items-center space-x-2">
                                <div className="w-8 h-8 bg-primary/20 rounded-full flex items-center justify-center">
                                  <Icon name="User" size={16} className="text-primary" />
                                </div>
                                <span className="font-medium text-text-primary text-sm">
                                  {comment.author_name}
                                </span>
                              </div>
                              <span className="text-xs text-text-secondary font-caption">
                                {formatDate(comment.created_at)}
                              </span>
                            </div>

                            <p className="text-text-primary text-sm mb-3 leading-relaxed">
                              {comment.content}
                            </p>

                            <div className="flex items-center space-x-4">
                              <Button
                                variant="ghost"
                                size="xs"
                                iconName="Heart"
                                onClick={() => handleCommentLike(comment.id)}
                                className="text-text-secondary hover:text-error"
                              >
                                {comment.like_count || 0}
                              </Button>

                              <Button
                                variant="ghost"
                                size="xs"
                                iconName="Reply"
                                onClick={() => setReplyTo(comment.id)}
                                className="text-text-secondary hover:text-primary"
                              >
                                Reply
                              </Button>
                            </div>

                            {/* Reply Form */}
                            {replyTo === comment.id && (
                              <div className="mt-4 ml-6 p-3 bg-surface/50 rounded-lg">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-3 mb-3">
                                  <Input
                                    type="text"
                                    placeholder="Your name *"
                                    value={replyData.author_name}
                                    onChange={(e) => setReplyData(prev => ({ ...prev, author_name: e.target.value }))}
                                    size="sm"
                                  />
                                  <Input
                                    type="email"
                                    placeholder="Your email"
                                    value={replyData.author_email}
                                    onChange={(e) => setReplyData(prev => ({ ...prev, author_email: e.target.value }))}
                                    size="sm"
                                  />
                                </div>

                                <textarea
                                  value={replyData.content}
                                  onChange={(e) => setReplyData(prev => ({ ...prev, content: e.target.value }))}
                                  placeholder="Write a reply..."
                                  rows={3}
                                  className="w-full px-3 py-2 bg-background border border-border-accent/40 rounded-lg text-text-primary placeholder-text-secondary focus:border-primary/60 focus:outline-none resize-none text-sm mb-3"
                                />

                                <div className="flex justify-end space-x-2">
                                  <Button
                                    variant="ghost"
                                    size="xs"
                                    onClick={() => {
                                      setReplyTo(null);
                                      setReplyData({ author_name: '', author_email: '', content: '' });
                                    }}
                                  >
                                    Cancel
                                  </Button>
                                  <Button
                                    variant="primary"
                                    size="xs"
                                    onClick={() => handleReplySubmit(comment.id)}
                                    disabled={commentLoading || !replyData.content.trim() || !replyData.author_name.trim()}
                                  >
                                    Reply
                                  </Button>
                                </div>
                              </div>
                            )}

                            {/* Replies */}
                            {comment.replies?.map((reply) => (
                              <div key={reply.id} className="ml-6 mt-4 bg-surface/50 rounded-lg p-3">
                                <div className="flex items-center justify-between mb-2">
                                  <div className="flex items-center space-x-2">
                                    <div className="w-6 h-6 bg-secondary/20 rounded-full flex items-center justify-center">
                                      <Icon name="User" size={12} className="text-secondary" />
                                    </div>
                                    <span className="font-medium text-text-primary text-xs">
                                      {reply.author_name}
                                    </span>
                                  </div>
                                  <span className="text-xs text-text-secondary font-caption">
                                    {formatDate(reply.created_at)}
                                  </span>
                                </div>
                                <p className="text-text-primary text-xs leading-relaxed">
                                  {reply.content}
                                </p>
                              </div>
                            ))}
                          </div>
                        ))
                      ) : (
                        <div className="text-center py-8">
                          <Icon name="MessageCircle" size={32} className="mx-auto mb-2 text-text-secondary" />
                          <p className="text-text-secondary text-sm">
                            No comments yet. Be the first to share your thoughts!
                          </p>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default BlogModal;

import React, { useEffect, useRef } from 'react';
import { loadExternalScript } from '../../../utils/scriptLoader';

// Renders custom/Yllix ad units. For script-based widgets, we optionally load a script URL.
const CustomAdUnit = ({ scriptUrl, html, className = '' }) => {
  const containerRef = useRef(null);

  useEffect(() => {
    let canceled = false;
    (async () => {
      if (scriptUrl) {
        await loadExternalScript(scriptUrl);
      }
      if (!canceled && containerRef.current && html) {
        // Insert provided HTML snippet into container
        containerRef.current.innerHTML = html;
      }
    })();
    return () => { canceled = true; };
  }, [scriptUrl, html]);

  if (!scriptUrl && !html) return null;
  return (
    <div className={`bg-surface border border-border-accent/20 rounded-lg overflow-hidden ${className}`}>
      <div ref={containerRef} />
    </div>
  );
};

export default CustomAdUnit;

import React, { useState, useEffect } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';

const SearchHeader = ({ 
  onSearch, 
  onSort, 
  searchQuery, 
  sortBy, 
  totalPosts, 
  onRefresh, 
  loading 
}) => {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [localSearchQuery, setLocalSearchQuery] = useState(searchQuery || '');
  const [showFilters, setShowFilters] = useState(false);

  useEffect(() => {
    setLocalSearchQuery(searchQuery || '');
  }, [searchQuery]);

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    onSearch(localSearchQuery);
    if (window.innerWidth < 768) {
      setIsSearchOpen(false);
    }
  };

  const handleClearSearch = () => {
    setLocalSearchQuery('');
    onSearch('');
    setIsSearchOpen(false);
  };

  const sortOptions = [
    { value: 'newest', label: 'Newest First', icon: 'CalendarDays' },
    { value: 'oldest', label: 'Oldest First', icon: 'Calendar' },
    { value: 'most-liked', label: 'Most Liked', icon: 'Heart' },
    { value: 'most-commented', label: 'Most Discussed', icon: 'MessageCircle' },
    { value: 'relevance', label: 'Most Relevant', icon: 'TrendingUp' }
  ];

  const currentSort = sortOptions.find(option => option.value === sortBy) || sortOptions[0];

  return (
    <div className="bg-surface border-b border-border-accent/20 sticky top-0 z-30 backdrop-blur-md">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between py-4">
          {/* Left Section - Title and Stats */}
          <div className="flex items-center space-x-4">
            <div>
              <h1 className="font-heading font-bold text-2xl text-text-primary">
                Blog Hub
              </h1>
              <p className="text-text-secondary text-sm font-caption">
                {loading ? (
                  <span className="flex items-center space-x-2">
                    <Icon name="Loader2" size={14} className="animate-spin" />
                    <span>Loading posts...</span>
                  </span>
                ) : (
                  <>
                    {totalPosts} {totalPosts === 1 ? 'post' : 'posts'} available
                    {searchQuery && (
                      <span className="ml-2 text-primary">
                         Filtered by "{searchQuery}"
                      </span>
                    )}
                  </>
                )}
              </p>
            </div>
          </div>

          {/* Right Section - Search and Controls */}
          <div className="flex items-center space-x-3">
            {/* Refresh Button */}
            <Button
              variant="ghost"
              size="sm"
              iconName="RefreshCw"
              onClick={onRefresh}
              disabled={loading}
              className={`text-text-secondary hover:text-primary ${loading ? 'animate-spin' : ''}`}
              title="Refresh posts"
            />

            {/* Desktop Search */}
            <div className="hidden md:block">
              <form onSubmit={handleSearchSubmit} className="relative">
                <Input
                  type="text"
                  placeholder="Search posts, tags, categories..."
                  value={localSearchQuery}
                  onChange={(e) => setLocalSearchQuery(e.target.value)}
                  className="w-80 pl-10 pr-10"
                />
                <Icon 
                  name="Search" 
                  size={18} 
                  className="absolute left-3 top-1/2 transform -translate-y-1/2 text-text-secondary" 
                />
                {localSearchQuery && (
                  <Button
                    type="button"
                    variant="ghost"
                    size="xs"
                    iconName="X"
                    onClick={handleClearSearch}
                    className="absolute right-2 top-1/2 transform -translate-y-1/2 text-text-secondary hover:text-primary"
                  />
                )}
              </form>
            </div>

            {/* Mobile Search Toggle */}
            <Button
              variant="ghost"
              size="sm"
              iconName="Search"
              onClick={() => setIsSearchOpen(!isSearchOpen)}
              className={`md:hidden ${isSearchOpen ? 'text-primary' : 'text-text-secondary hover:text-primary'}`}
              title="Toggle search"
            />

            {/* Sort Dropdown */}
            <div className="relative">
              <Button
                variant="ghost"
                size="sm"
                iconName={currentSort.icon}
                iconPosition="left"
                onClick={() => setShowFilters(!showFilters)}
                className="text-text-secondary hover:text-primary hidden sm:flex"
                title="Sort options"
              >
                {currentSort.label}
                <Icon name="ChevronDown" size={16} className="ml-1" />
              </Button>

              {/* Mobile Sort Button */}
              <Button
                variant="ghost"
                size="sm"
                iconName={currentSort.icon}
                onClick={() => setShowFilters(!showFilters)}
                className="text-text-secondary hover:text-primary sm:hidden"
                title="Sort options"
              />

              {/* Sort Dropdown Menu */}
              {showFilters && (
                <div className="absolute right-0 mt-2 w-56 bg-surface border border-border-accent/20 rounded-lg shadow-lg z-40">
                  <div className="py-2">
                    <div className="px-3 py-2 text-xs font-medium text-text-secondary uppercase tracking-wide border-b border-border-accent/20">
                      Sort by
                    </div>
                    {sortOptions.map((option) => (
                      <button
                        key={option.value}
                        onClick={() => {
                          onSort(option.value);
                          setShowFilters(false);
                        }}
                        className={`w-full flex items-center space-x-3 px-3 py-2 text-sm transition-colors duration-fast ${
                          sortBy === option.value
                            ? 'bg-primary/10 text-primary' :'text-text-primary hover:bg-accent/10 hover:text-accent'
                        }`}
                      >
                        <Icon name={option.icon} size={16} />
                        <span>{option.label}</span>
                        {sortBy === option.value && (
                          <Icon name="Check" size={14} className="ml-auto" />
                        )}
                      </button>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* Filters Toggle (Mobile) */}
            <Button
              variant="ghost"
              size="sm"
              iconName="Filter"
              onClick={() => setShowFilters(!showFilters)}
              className="text-text-secondary hover:text-primary md:hidden"
              title="Filter options"
            />
          </div>
        </div>

        {/* Mobile Search Bar */}
        {isSearchOpen && (
          <div className="md:hidden pb-4">
            <form onSubmit={handleSearchSubmit} className="relative">
              <Input
                type="text"
                placeholder="Search posts, tags, categories..."
                value={localSearchQuery}
                onChange={(e) => setLocalSearchQuery(e.target.value)}
                className="w-full pl-10 pr-10"
                autoFocus
              />
              <Icon 
                name="Search" 
                size={18} 
                className="absolute left-3 top-1/2 transform -translate-y-1/2 text-text-secondary" 
              />
              {localSearchQuery && (
                <Button
                  type="button"
                  variant="ghost"
                  size="xs"
                  iconName="X"
                  onClick={handleClearSearch}
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 text-text-secondary hover:text-primary"
                />
              )}
            </form>
          </div>
        )}

        {/* Active Filters Display */}
        {(searchQuery || sortBy !== 'newest') && (
          <div className="pb-4">
            <div className="flex items-center space-x-2 flex-wrap">
              <span className="text-xs text-text-secondary font-medium">Active filters:</span>

              {searchQuery && (
                <div className="flex items-center space-x-1 bg-primary/10 text-primary px-2 py-1 rounded-md text-xs">
                  <Icon name="Search" size={12} />
                  <span>"{searchQuery}"</span>
                  <Button
                    variant="ghost"
                    size="xs"
                    iconName="X"
                    onClick={() => onSearch('')}
                    className="text-primary hover:text-primary/80"
                  />
                </div>
              )}

              {sortBy !== 'newest' && (
                <div className="flex items-center space-x-1 bg-accent/10 text-accent px-2 py-1 rounded-md text-xs">
                  <Icon name={currentSort.icon} size={12} />
                  <span>{currentSort.label}</span>
                  <Button
                    variant="ghost"
                    size="xs"
                    iconName="X"
                    onClick={() => onSort('newest')}
                    className="text-accent hover:text-accent/80"
                  />
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Click outside to close filters */}
      {showFilters && (
        <div 
          className="fixed inset-0 z-30" 
          onClick={() => setShowFilters(false)}
        ></div>
      )}
    </div>
  );
};

export default SearchHeader;

import React, { useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';

const ShareModal = ({ isOpen, onClose, post }) => {
  const [copied, setCopied] = useState(false);

  if (!isOpen || !post) return null;

  const shareUrl = `${window.location.origin}/blog/${post.id}`;
  const shareText = `Check out this blog post: ${post.title}`;

  const socialPlatforms = [
    {
      name: 'Twitter',
      icon: 'Twitter',
      url: `https://twitter.com/intent/tweet?text=${encodeURIComponent(shareText)}&url=${encodeURIComponent(shareUrl)}`,
      color: 'text-blue-400 hover:text-blue-300'
    },
    {
      name: 'LinkedIn',
      icon: 'Linkedin',
      url: `https://www.linkedin.com/sharing/share-offsite/?url=${encodeURIComponent(shareUrl)}`,
      color: 'text-blue-600 hover:text-blue-500'
    },
    {
      name: 'Facebook',
      icon: 'Facebook',
      url: `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(shareUrl)}`,
      color: 'text-blue-500 hover:text-blue-400'
    },
    {
      name: 'Reddit',
      icon: 'MessageSquare',
      url: `https://reddit.com/submit?url=${encodeURIComponent(shareUrl)}&title=${encodeURIComponent(post.title)}`,
      color: 'text-orange-500 hover:text-orange-400'
    }
  ];

  const handleCopyLink = async () => {
    try {
      await navigator.clipboard.writeText(shareUrl);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy link:', err);
    }
  };

  const handleSocialShare = (url) => {
    window.open(url, '_blank', 'width=600,height=400,scrollbars=yes,resizable=yes');
  };

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
        {/* Backdrop */}
        <div 
          className="fixed inset-0 bg-background/80 backdrop-blur-sm transition-opacity"
          onClick={onClose}
        ></div>

        {/* Modal */}
        <div className="inline-block align-bottom bg-surface rounded-lg border border-border-accent/20 text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-md sm:w-full">
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-border-accent/20">
            <h3 className="font-heading font-semibold text-lg text-text-primary">
              Share Post
            </h3>
            <Button
              variant="ghost"
              size="sm"
              iconName="X"
              onClick={onClose}
              className="text-text-secondary hover:text-primary"
            />
          </div>

          {/* Content */}
          <div className="p-6">
            {/* Post Preview */}
            <div className="mb-6 p-4 bg-background/50 rounded-lg border border-border-accent/20">
              <h4 className="font-medium text-text-primary text-sm mb-2 line-clamp-2">
                {post.title}
              </h4>
              <p className="text-text-secondary text-xs line-clamp-2">
                {post.excerpt}
              </p>
            </div>

            {/* Copy Link */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-text-primary mb-2">
                Copy Link
              </label>
              <div className="flex space-x-2">
                <Input
                  type="text"
                  value={shareUrl}
                  readOnly
                  className="flex-1 bg-background/50 text-text-secondary text-sm"
                />
                <Button
                  variant={copied ? "success" : "outline"}
                  size="sm"
                  iconName={copied ? "Check" : "Copy"}
                  onClick={handleCopyLink}
                  className="min-w-[80px]"
                >
                  {copied ? "Copied!" : "Copy"}
                </Button>
              </div>
            </div>

            {/* Social Platforms */}
            <div>
              <label className="block text-sm font-medium text-text-primary mb-3">
                Share on Social Media
              </label>
              <div className="grid grid-cols-2 gap-3">
                {socialPlatforms.map((platform) => (
                  <Button
                    key={platform.name}
                    variant="outline"
                    size="sm"
                    onClick={() => handleSocialShare(platform.url)}
                    className={`
                      justify-start space-x-2 hover:border-primary/40 transition-all duration-fast
                      ${platform.color}
                    `}
                  >
                    <Icon name={platform.icon} size={18} />
                    <span>{platform.name}</span>
                  </Button>
                ))}
              </div>
            </div>

            {/* Email Share */}
            <div className="mt-6 pt-6 border-t border-border-accent/20">
              <Button
                variant="ghost"
                size="sm"
                iconName="Mail"
                onClick={() => {
                  const subject = encodeURIComponent(`Check out: ${post.title}`);
                  const body = encodeURIComponent(`I thought you might be interested in this blog post:\n\n${post.title}\n${shareUrl}`);
                  window.location.href = `mailto:?subject=${subject}&body=${body}`;
                }}
                className="w-full justify-center text-text-secondary hover:text-primary hover:bg-primary/10"
              >
                Share via Email
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShareModal;

import React, { useState, useEffect, useCallback } from 'react';
import { Helmet } from 'react-helmet';
import { useAuth } from '../../contexts/AuthContext';
import HeaderNavigation from '../../components/ui/HeaderNavigation';
import SearchHeader from './components/SearchHeader';
import BlogGrid from './components/BlogGrid';
import BlogModal from './components/BlogModal';
import ShareModal from './components/ShareModal';
import Button from '../../components/ui/Button';
import Icon from '../../components/AppIcon';
import blogService from '../../utils/blogService';
import settingsService from '../../utils/settingsService';
import { ensureAdSenseLoaded } from '../../utils/adsense';

const BlogContentHub = () => {
  const { user, userProfile } = useAuth();
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('newest');
  const [selectedPost, setSelectedPost] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isShareModalOpen, setIsShareModalOpen] = useState(false);
  const [sharePost, setSharePost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);
  const [adsConfig, setAdsConfig] = useState({ enabled: false });

  const isAdmin = userProfile?.role === 'admin';

  // Load blog posts
  const loadPosts = useCallback(async (options = {}) => {
    try {
      setLoading(true);
      setError(null);

      const result = isAdmin 
        ? await blogService.getAllPosts()
        : await blogService.getPublishedPosts({
            search: searchQuery,
            sortBy,
            ...options
          });

      if (result.success) {
        setPosts(result.data || []);
      } else {
        setError(result.error || 'Failed to load blog posts');
        setPosts([]);
      }
    } catch (err) {
      setError('Network error occurred while loading posts');
      setPosts([]);
    } finally {
      setLoading(false);
    }
  }, [isAdmin, searchQuery, sortBy]);

  // Initial load
  useEffect(() => {
    loadPosts();
    // Load ads settings
    (async () => {
      const res = await settingsService.getSettings();
      if (res.success) {
        const ads = res.data?.ads_settings || {};
        setAdsConfig({
          enabled: !!ads.enabled,
          provider: ads.provider || 'adsense',
          publisher_id: ads.publisher_id || '',
          auto_ads: ads.auto_ads !== false,
          grid_interval: Number(ads.grid_interval) || 6,
          ad_slot: ads.ad_slot || '',
          // yllix/custom
          yllix_publisher_id: ads.yllix_publisher_id || '',
          yllix_script_url: ads.yllix_script_url || '',
          yllix_unit_code: ads.yllix_unit_code || '',
          custom_script_url: ads.custom_script_url || '',
          custom_ad_html: ads.custom_ad_html || ''
        });
        if (ads.enabled && (ads.provider || 'adsense') === 'adsense' && ads.publisher_id) {
          await ensureAdSenseLoaded(ads.publisher_id);
        }
      }
    })();
  }, [loadPosts]);

  // Reload when search or sort changes
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      if (searchQuery !== undefined) {
        loadPosts();
      }
    }, 300); // Debounce search

    return () => clearTimeout(timeoutId);
  }, [searchQuery, sortBy, loadPosts]);

  // Filter and sort posts locally for better UX
  const filteredAndSortedPosts = useCallback(() => {
    let filtered = [...posts];

    // Apply search filter locally for immediate response
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(post =>
        post?.title?.toLowerCase().includes(query) ||
        post?.excerpt?.toLowerCase().includes(query) ||
        post?.content?.toLowerCase().includes(query) ||
        post?.tags?.some(tag => tag?.toLowerCase().includes(query)) ||
        post?.category?.toLowerCase().includes(query)
      );
    }

    // Apply sorting
    switch (sortBy) {
      case 'newest':
        filtered.sort((a, b) => new Date(b.published_at || b.created_at) - new Date(a.published_at || a.created_at));
        break;
      case 'oldest':
        filtered.sort((a, b) => new Date(a.published_at || a.created_at) - new Date(b.published_at || b.created_at));
        break;
      case 'most-liked':
        filtered.sort((a, b) => (b.like_count || 0) - (a.like_count || 0));
        break;
      case 'most-commented':
        filtered.sort((a, b) => (b.comment_count || 0) - (a.comment_count || 0));
        break;
      case 'relevance':
        filtered.sort((a, b) => {
          const scoreA = (a.like_count || 0) * 2 + (a.comment_count || 0) * 3 + (a.view_count || 0) * 0.1;
          const scoreB = (b.like_count || 0) * 2 + (b.comment_count || 0) * 3 + (b.view_count || 0) * 0.1;
          return scoreB - scoreA;
        });
        break;
      default:
        break;
    }

    return filtered;
  }, [posts, searchQuery, sortBy]);

  const handleSearch = useCallback((query) => {
    setSearchQuery(query);
  }, []);

  const handleSort = useCallback((sortOption) => {
    setSortBy(sortOption);
  }, []);

  const handleLike = useCallback(async (postId) => {
    try {
      // Get visitor IP for like tracking
      const visitorIp = await getVisitorIp();
      const userAgent = navigator.userAgent || '';

      const result = await blogService.toggleLike(postId, visitorIp, userAgent);

      if (result.success) {
        // Update local state optimistically
        setPosts(prevPosts => 
          prevPosts.map(post => 
            post.id === postId 
              ? { 
                  ...post, 
                  like_count: result.liked 
                    ? (post.like_count || 0) + 1 
                    : Math.max((post.like_count || 0) - 1, 0)
                }
              : post
          )
        );
      }
    } catch (error) {
      console.log('Failed to toggle like:', error);
    }
  }, []);

  const handleShare = useCallback((post) => {
    setSharePost(post);
    setIsShareModalOpen(true);
  }, []);

  const handleEdit = useCallback((post) => {
    setSelectedPost(post);
    setIsModalOpen(true);
  }, []);

  const handleDelete = useCallback(async (postId) => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      try {
        const result = await blogService.deletePost(postId);

        if (result.success) {
          setPosts(prevPosts => prevPosts.filter(post => post.id !== postId));
        } else {
          setError(result.error || 'Failed to delete post');
        }
      } catch (error) {
        setError('Network error occurred while deleting post');
      }
    }
  }, []);

  const handleSave = useCallback(async (updatedPost) => {
    try {
      let result;

      if (updatedPost.id && posts.find(p => p.id === updatedPost.id)) {
        // Update existing post
        result = await blogService.updatePost(updatedPost.id, updatedPost);
      } else {
        // Create new post
        result = await blogService.createPost(updatedPost);
      }

      if (result.success) {
        await loadPosts(); // Refresh posts
      } else {
        setError(result.error || 'Failed to save post');
      }
    } catch (error) {
      setError('Network error occurred while saving post');
    }
  }, [posts, loadPosts]);

  const handlePostClick = useCallback(async (post) => {
    try {
      // For detailed view, get the full post with comments
      const result = await blogService.getPostBySlug(post.slug);

      if (result.success) {
        setSelectedPost({
          ...post,
          comments: result.data.comments || []
        });
      } else {
        setSelectedPost(post);
      }

      setIsModalOpen(true);
    } catch (error) {
      // Fallback to original post data
      setSelectedPost(post);
      setIsModalOpen(true);
    }
  }, []);

  // Get visitor IP helper function
  const getVisitorIp = async () => {
    try {
      const response = await fetch('https://api.ipify.org?format=json');
      const data = await response.json();
      return data.ip || 'unknown';
    } catch (error) {
      return `fallback-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    }
  };

  const displayedPosts = filteredAndSortedPosts();

  return (
    <div className="min-h-screen bg-background">
      <Helmet>
        <title>Mikael Kraft  Blog</title>
        <meta name="description" content="Read articles by Mikael Kraft on software engineering, web development, and technology insights." />
        <meta property="og:title" content="Mikael Kraft  Blog" />
        <meta property="og:description" content="Latest posts on fullstack engineering, security, and practical tech writeups." />
        <meta property="og:type" content="website" />
        <meta property="og:url" content="https://mkraft.tech/blog" />
        <meta property="og:image" content="/assets/images/no_image.png" />
        <meta name="twitter:card" content="summary_large_image" />
        <meta name="twitter:site" content="@mikael_kraft" />
        <meta name="twitter:creator" content="@mikael_kraft" />
        <link rel="canonical" href="https://mkraft.tech/blog" />
      </Helmet>
      {/* Header Navigation */}
      <HeaderNavigation />

      {/* Search Header */}
      <SearchHeader
        onSearch={handleSearch}
        onSort={handleSort}
        searchQuery={searchQuery}
        sortBy={sortBy}
        totalPosts={posts.length}
        onRefresh={() => loadPosts()}
        loading={loading}
      />

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Error Message */}
        {error && (
          <div className="mb-8 p-4 bg-error/10 border border-error/20 rounded-lg">
            <div className="flex items-center space-x-2">
              <Icon name="AlertCircle" size={20} className="text-error" />
              <p className="text-error font-medium">{error}</p>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setError(null)}
                iconName="X"
                className="ml-auto text-error hover:text-error/80"
              />
            </div>
            <Button
              variant="outline"
              size="sm"
              onClick={() => loadPosts()}
              className="mt-2"
              iconName="RefreshCw"
              iconPosition="left"
            >
              Retry
            </Button>
          </div>
        )}

        {/* Add New Post Button (Admin Only) */}
        {isAdmin && (
          <div className="mb-8 flex justify-end">
            <Button
              variant="primary"
              iconName="Plus"
              iconPosition="left"
              onClick={() => {
                const newPost = {
                  id: null, // Will be set by database
                  title: '',
                  excerpt: '',
                  content: '',
                  category: '',
                  tags: [],
                  featured_image: '',
                  status: 'draft',
                  author_id: user?.id,
                  author: userProfile?.full_name || 'Admin',
                  published_at: null,
                  created_at: new Date().toISOString()
                };
                setSelectedPost(newPost);
                setIsModalOpen(true);
              }}
              className="shadow-glow-primary"
            >
              Create New Post
            </Button>
          </div>
        )}

        {/* Blog Grid */}
        <BlogGrid
          posts={displayedPosts}
          onLike={handleLike}
          onShare={handleShare}
          onPostClick={handlePostClick}
          isAdmin={isAdmin}
          onEdit={handleEdit}
          onDelete={handleDelete}
          loading={loading}
          adsConfig={adsConfig}
        />

        {/* Load More Button (if needed for pagination) */}
        {displayedPosts.length > 0 && displayedPosts.length % 6 === 0 && (
          <div className="text-center mt-12">
            <Button
              variant="outline"
              iconName="ChevronDown"
              iconPosition="right"
              onClick={() => {
                loadPosts({ 
                  offset: posts.length,
                  limit: 6 
                });
              }}
              className="hover:border-primary/60 hover:text-primary"
              disabled={loading}
            >
              {loading ? 'Loading...' : 'Load More Posts'}
            </Button>
          </div>
        )}

        {/* Empty State for No Results */}
        {displayedPosts.length === 0 && !loading && (
          <div className="text-center py-16">
            <div className="max-w-md mx-auto">
              <Icon name="Search" size={48} className="mx-auto mb-4 text-text-secondary" />
              <h3 className="font-heading font-semibold text-xl text-text-primary mb-2">
                {searchQuery ? 'No posts found' : 'No blog posts yet'}
              </h3>
              <p className="text-text-secondary mb-6">
                {searchQuery 
                  ? `No blog posts match your search for "${searchQuery}". Try different keywords or browse all posts.`
                  : 'No blog posts have been published yet. Check back later for updates.'
                }
              </p>
              {searchQuery ? (
                <Button
                  variant="outline"
                  onClick={() => setSearchQuery('')}
                  iconName="RotateCcw"
                  iconPosition="left"
                >
                  Clear Search
                </Button>
              ) : isAdmin && (
                <Button
                  variant="primary"
                  onClick={() => {
                    const newPost = {
                      id: null,
                      title: '',
                      excerpt: '',
                      content: '',
                      category: '',
                      tags: [],
                      featured_image: '',
                      status: 'draft',
                      author_id: user?.id,
                      author: userProfile?.full_name || 'Admin'
                    };
                    setSelectedPost(newPost);
                    setIsModalOpen(true);
                  }}
                  iconName="Plus"
                  iconPosition="left"
                >
                  Create First Post
                </Button>
              )}
            </div>
          </div>
        )}
      </main>

      {/* Blog Modal */}
      <BlogModal
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setSelectedPost(null);
        }}
        post={selectedPost}
        onSave={handleSave}
        isAdmin={isAdmin}
      />

      {/* Share Modal */}
      <ShareModal
        isOpen={isShareModalOpen}
        onClose={() => {
          setIsShareModalOpen(false);
          setSharePost(null);
        }}
        post={sharePost}
      />

      {/* Scroll to Top Button */}
      <div className="fixed bottom-6 right-6 z-40">
        <Button
          variant="primary"
          size="sm"
          iconName="ArrowUp"
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
          className="rounded-full w-12 h-12 shadow-glow-primary hover:shadow-glow-secondary transition-all duration-fast"
          title="Scroll to top"
        />
      </div>

      {/* Footer */}
      <footer className="bg-surface border-t border-border-accent/20 mt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center">
            <div className="flex items-center justify-center space-x-2 mb-4">
              <Icon name="Zap" size={20} className="text-primary" />
              <span className="font-heading font-bold text-lg text-primary">
                WisdomInTech
              </span>
            </div>
            <p className="text-text-secondary text-sm font-caption">
               {new Date().getFullYear()} Built with love  by Mikael Kraft
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
};

