                      <span>{post.like_count || 0}</span>
                    </div>
                  </div>
                  <span>
                    {new Date(post.published_at || post.created_at).toLocaleDateString()}
                  </span>
                </div>

                {/* Tags */}
                {post.tags && post.tags.length > 0 && (
                  <div className="flex flex-wrap gap-1 mt-4">
                    {post.tags.slice(0, 3).map((tag, tagIndex) => (
                      <span
                        key={tagIndex}
                        className={`px-2 py-1 ${themeClasses.accent} bg-opacity-10 rounded-full text-xs font-caption border border-current border-opacity-20`}
                      >
                        {tag}
                      </span>
                    ))}
                    {post.tags.length > 3 && (
                      <span className={`px-2 py-1 ${themeClasses.text} opacity-50 text-xs`}>
                        +{post.tags.length - 3}
                      </span>
                    )}
                  </div>
                )}
              </div>
            </Link>
          ))}
        </div>

        {/* View All Blog Posts Button */}
        <div className="text-center">
          <Link
            to="/blog-content-hub"
            className={`inline-flex items-center px-8 py-4 ${themeClasses.cardBg} border rounded-lg font-medium transition-all duration-fast hover:scale-105 hover-glow-primary group`}
          >
            <span className={themeClasses.text}>View All Blog Posts</span>
            <Icon
              name="ArrowRight"
              size={20}
              className={`ml-2 ${themeClasses.accent} group-hover:translate-x-1 transition-transform duration-fast`}
            />
          </Link>
        </div>
      </div>
    </section>
  );
};

export default FeaturedBlogPosts;

import React, { useState, useEffect } from 'react';
import Icon from '../../../components/AppIcon';
import Image from '../../../components/AppImage';
import settingsService from '../../../utils/settingsService';

const HeroSection = ({ currentTheme }) => {
  const [typedText, setTypedText] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [settings, setSettings] = useState({});

  useEffect(() => {
    const loadSettings = async () => {
      try {
        const res = await settingsService.getSettings();
        if (res.success) {
          setSettings(res.data);
        }
      } catch (error) {
        console.error('Failed to load settings:', error);
      }
    };
    loadSettings();
  }, []);

  // Utility to extract filename from URL
  const getFilenameFromUrl = (url) => {
    try {
      const urlObj = new URL(url, window.location.origin);
      const pathname = urlObj.pathname;
      const filename = pathname.substring(pathname.lastIndexOf('/') + 1) || 'resume.pdf';
      return filename;
    } catch (e) {
      return 'resume.pdf';
    }
  };

  const handleDownloadResume = () => {
    if (settings.resumeUrl) {
      // Determine filename: use settings.resumeFilename if available, else derive from URL
      const filename = settings.resumeFilename || getFilenameFromUrl(settings.resumeUrl);
      // Create a temporary link element to trigger download
      const link = document.createElement('a');
      link.href = settings.resumeUrl;
      link.download = filename;
      link.target = '_blank';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  const phrases = [
    "React Developer",
    "Node.js Developer", 
    "Full-Stack Engineer",
    "JavaScript Expert",
    "React Specialist"
  ];

  useEffect(() => {
    const typeSpeed = isDeleting ? 50 : 100;
    const currentPhrase = phrases[currentIndex];

    const timeout = setTimeout(() => {
      if (!isDeleting && typedText === currentPhrase) {
        setTimeout(() => setIsDeleting(true), 2000);
      } else if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setCurrentIndex((prev) => (prev + 1) % phrases.length);
      } else {
        setTypedText(prev => 
          isDeleting 
            ? prev.slice(0, -1)
            : currentPhrase.slice(0, prev.length + 1)
        );
      }
    }, typeSpeed);

    return () => clearTimeout(timeout);
  }, [typedText, currentIndex, isDeleting, phrases]);

  const getThemeClasses = () => {
    switch(currentTheme) {
      case 'neural':
        return 'bg-gradient-to-br from-purple-900/20 to-blue-900/20 border-purple-500/30';
      case 'futuristic':
        return 'bg-gradient-to-br from-slate-900/20 to-blue-900/20 border-blue-400/30';
      case 'light':
        return 'bg-gradient-to-br from-gray-50 to-blue-50 border-gray-300';
      default: // cyberpunk
        return 'bg-gradient-to-br from-surface/20 to-primary/5 border-primary/30';
    }
  };

  const getTextClasses = () => {
    switch(currentTheme) {
      case 'light':
        return {
          primary: 'text-gray-900',
          secondary: 'text-gray-600',
          accent: 'text-blue-600'
        };
      default:
        return {
          primary: 'text-text-primary',
          secondary: 'text-text-secondary', 
          accent: 'text-primary'
        };
    }
  };

  const textColors = getTextClasses();

  return (
    <section className={`min-h-screen flex items-center justify-center px-6 py-20 relative overflow-hidden ${currentTheme === 'light' ? 'bg-white' : 'bg-background'}`}>
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {currentTheme !== 'light' && (
          <>
            <div className="absolute top-1/4 left-1/4 w-64 h-64 bg-primary/5 rounded-full blur-3xl animate-pulse"></div>
            <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-secondary/5 rounded-full blur-3xl animate-pulse delay-1000"></div>
            <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-full h-full cyber-grid opacity-10"></div>
          </>
        )}
      </div>

      <div className="max-w-6xl mx-auto text-center relative z-10">
        {/* Profile Image */}
  <div className="mt-8 md:mt-16 mb-8 flex justify-center">
          <div className={`relative w-32 h-32 lg:w-40 lg:h-40 rounded-full overflow-hidden border-4 ${currentTheme === 'light' ? 'border-blue-200' : 'border-primary/50'} hover-glow-primary transition-all duration-normal`}>
            <Image 
              src={settings.heroImageUrl || settings.hero_image_url || "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=400&fit=crop&crop=face"}
              alt="Mikael Kraft - Full Stack Developer"
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-primary/20 to-transparent"></div>
          </div>
        </div>

        {/* Main Heading */}
        <h1 className={`font-heading text-4xl lg:text-6xl xl:text-7xl font-bold mb-6 ${textColors.primary}`}>
          <span className="block">Mikael</span>
          <span className={`block bg-gradient-to-r ${currentTheme === 'light' ? 'from-blue-600 to-purple-600' : 'from-primary to-secondary'} bg-clip-text text-transparent`}>
            Kraft
          </span>
        </h1>

        {/* Typing Animation */}
        <div className="mb-8 h-16 flex items-center justify-center">
          <h2 className={`font-heading text-xl lg:text-2xl xl:text-3xl ${textColors.accent}`}>
            {typedText}
            <span className="animate-pulse">|</span>
          </h2>
        </div>

        {/* Description */}
        <div className={`max-w-4xl mx-auto mb-12 space-y-4 ${textColors.secondary}`}>
          <p className="text-lg lg:text-xl leading-relaxed">
            Crafting exceptional web applications with React and Node.js.
            I focus on building scalable, performant solutions that deliver 
            outstanding user experiences through modern JavaScript technologies.
          </p>
          <p className="text-base lg:text-lg">
            From interactive React frontends to robust Node.js backends, I create 
            full-stack applications that seamlessly connect users with technology.
          </p>
        </div>

        {/* CTA Buttons */}
        <div className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-16">
          <button className={`
            px-8 py-4 rounded-lg font-medium text-lg transition-all duration-fast
            ${currentTheme === 'light' ?'bg-blue-600 text-white hover:bg-blue-700 shadow-lg hover:shadow-xl' :'bg-primary text-background hover:bg-primary/90 glow-primary hover:shadow-glow-primary'
            }
            min-h-[48px] min-w-[160px]
          `}>
            <Icon name="FolderOpen" size={20} className="inline mr-2" />
            View Projects
          </button>
          <button 
            onClick={handleDownloadResume}
            disabled={!settings.resumeUrl}
            className={`
              px-8 py-4 rounded-lg font-medium text-lg transition-all duration-fast border-2
              ${!settings.resumeUrl ? 'opacity-50 cursor-not-allowed' : ''}
              ${currentTheme === 'light' ?'border-blue-600 text-blue-600 hover:bg-blue-50' :'border-primary text-primary hover:bg-primary/10'
              }
              min-h-[48px] min-w-[160px]
            `}
          >
            <Icon name="Download" size={20} className="inline mr-2" />
            Download CV
          </button>
        </div>

        {/* Scroll Indicator */}
        <div className="flex justify-center">
          <div className={`animate-bounce ${textColors.secondary}`}>
            <Icon name="ChevronDown" size={24} />
          </div>
        </div>
      </div>

      {/* Floating Elements */}
      {currentTheme !== 'light' && (
        <div className="absolute inset-0 pointer-events-none">
          <div className="absolute top-20 left-10 text-primary/20 animate-pulse">
            <Icon name="Code" size={32} />
          </div>
          <div className="absolute top-40 right-20 text-secondary/20 animate-pulse delay-500">
            <Icon name="Shield" size={28} />
          </div>
          <div className="absolute bottom-40 left-20 text-accent/20 animate-pulse delay-1000">
            <Icon name="Database" size={30} />
          </div>
          <div className="absolute bottom-20 right-10 text-primary/20 animate-pulse delay-1500">
            <Icon name="Cpu" size={26} />
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;

import React, { useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import newsletterService from '../../../utils/newsletterService';

const NewsletterSubscription = ({ currentTheme }) => {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [success, setSuccess] = useState(false);

  const getThemeClasses = () => {
    switch(currentTheme) {
      case 'neural':
        return {
          bg: 'bg-gradient-to-br from-purple-900/10 to-blue-900/10',
          border: 'border-purple-500/20',
          text: 'text-purple-200',
          accent: 'text-purple-400',
          cardBg: 'bg-purple-900/20 border-purple-500/30',
          input: 'bg-purple-900/30 border-purple-500/30 text-purple-100 placeholder-purple-300/70 focus:border-purple-400'
        };
      case 'futuristic':
        return {
          bg: 'bg-gradient-to-br from-slate-900/10 to-blue-900/10',
          border: 'border-blue-400/20',
          text: 'text-blue-200',
          accent: 'text-blue-400',
          cardBg: 'bg-slate-900/20 border-blue-400/30',
          input: 'bg-slate-900/30 border-blue-400/30 text-blue-100 placeholder-blue-300/70 focus:border-blue-400'
        };
      case 'dark':
        return {
          bg: 'bg-gradient-to-br from-gray-900/10 to-blue-900/10',
          border: 'border-blue-500/20',
          text: 'text-blue-200',
          accent: 'text-blue-400',
          cardBg: 'bg-gray-900/20 border-blue-500/30',
          input: 'bg-gray-900/30 border-blue-500/30 text-blue-100 placeholder-blue-300/70 focus:border-blue-400'
        };
      case 'light':
        return {
          bg: 'bg-gradient-to-br from-gray-50 to-blue-50',
          border: 'border-gray-300',
          text: 'text-gray-700',
          accent: 'text-blue-600',
          cardBg: 'bg-white border-gray-200 shadow-lg',
          input: 'bg-white border-gray-300 text-gray-900 placeholder-gray-500 focus:border-blue-500'
        };
      default: // cyberpunk
        return {
          bg: 'bg-gradient-to-br from-surface/10 to-primary/5',
          border: 'border-primary/20',
          text: 'text-text-primary',
          accent: 'text-primary',
          cardBg: 'bg-surface/20 border-primary/30',
          input: 'bg-surface/30 border-primary/30 text-text-primary placeholder-text-secondary focus:border-primary'
        };
    }
  };

  const themeClasses = getThemeClasses();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email) {
      setMessage('Email is required');
      setSuccess(false);
      return;
    }

    setLoading(true);
    setMessage('');

    try {
      const result = await newsletterService.subscribe(email, name);

      if (result.success) {
        setMessage('Successfully subscribed to newsletter!');
        setSuccess(true);
        setEmail('');
        setName('');
      } else {
        setMessage(result.error || 'Failed to subscribe');
        setSuccess(false);
      }
    } catch (error) {
      setMessage('An error occurred. Please try again.');
      setSuccess(false);
    } finally {
      setLoading(false);
    }

    // Clear message after 5 seconds
    setTimeout(() => {
      setMessage('');
      setSuccess(false);
    }, 5000);
  };

  return (
    <section className={`py-20 ${themeClasses.bg} border-t ${themeClasses.border}`}>
      <div className="max-w-4xl mx-auto px-6">
        <div className={`${themeClasses.cardBg} border rounded-xl p-8 md:p-12 text-center`}>
          {/* Header */}
          <div className="mb-8">
            <div className={`w-16 h-16 mx-auto mb-6 rounded-full ${themeClasses.cardBg} border flex items-center justify-center`}>
              <Icon name="Mail" size={32} className={themeClasses.accent} />
            </div>
            <h2 className={`text-3xl md:text-4xl font-heading font-bold ${themeClasses.text} mb-4`}>
              Stay Updated
            </h2>
            <p className={`text-lg ${themeClasses.text} opacity-80 max-w-2xl mx-auto`}>
              Get the latest insights on technology, cybersecurity, and development directly in your inbox. 
              Join developers from around the world.
            </p>
          </div>

          {/* Subscription Form */}
          <form onSubmit={handleSubmit} className="max-w-md mx-auto space-y-4">
            <div className="flex flex-col sm:flex-row gap-4">
              <input
                type="text"
                placeholder="Your name (optional)"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className={`flex-1 px-4 py-3 rounded-lg border ${themeClasses.input} focus:outline-none focus:ring-2 focus:ring-opacity-50 transition-all duration-fast`}
                disabled={loading}
              />
            </div>

            <div className="flex flex-col sm:flex-row gap-4">
              <input
                type="email"
                placeholder="Enter your email address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className={`flex-1 px-4 py-3 rounded-lg border ${themeClasses.input} focus:outline-none focus:ring-2 focus:ring-opacity-50 transition-all duration-fast`}
                required
                disabled={loading}
              />
              <Button
                type="submit"
                variant="primary"
                disabled={loading}
                className="whitespace-nowrap"
                iconName={loading ? "Loader2" : "Send"}
                iconPosition="right"
              >
                {loading ? 'Subscribing...' : 'Subscribe'}
              </Button>
            </div>

            {/* Message */}
            {message && (
              <div className={`p-4 rounded-lg border text-sm ${
                success 
                  ? 'bg-green-500/10 border-green-500/30 text-green-400' 
                  : 'bg-red-500/10 border-red-500/30 text-red-400'
              }`}>
                <div className="flex items-center gap-2">
                  <Icon 
                    name={success ? "CheckCircle" : "AlertCircle"} 
                    size={16} 
                    className={success ? "text-green-400" : "text-red-400"} 
                  />
                  {message}
                </div>
              </div>
            )}
          </form>

          {/* Features */}
          <div className="mt-8 pt-8 border-t border-opacity-20 border-current">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-center">
              <div className="flex flex-col items-center">
                <div className={`w-10 h-10 rounded-full ${themeClasses.cardBg} border flex items-center justify-center mb-3`}>
                  <Icon name="Zap" size={16} className={themeClasses.accent} />
                </div>
                <h4 className={`font-semibold ${themeClasses.text} mb-1`}>Weekly Insights</h4>
                <p className={`text-sm ${themeClasses.text} opacity-70`}>Latest tech trends and tutorials</p>
              </div>

              <div className="flex flex-col items-center">
                <div className={`w-10 h-10 rounded-full ${themeClasses.cardBg} border flex items-center justify-center mb-3`}>
                  <Icon name="Shield" size={16} className={themeClasses.accent} />
                </div>
                <h4 className={`font-semibold ${themeClasses.text} mb-1`}>No Spam</h4>
                <p className={`text-sm ${themeClasses.text} opacity-70`}>Unsubscribe anytime, privacy protected</p>
              </div>

              <div className="flex flex-col items-center">
                <div className={`w-10 h-10 rounded-full ${themeClasses.cardBg} border flex items-center justify-center mb-3`}>
                  <Icon name="Users" size={16} className={themeClasses.accent} />
                </div>
                <h4 className={`font-semibold ${themeClasses.text} mb-1`}>Join 2.5K+</h4>
                <p className={`text-sm ${themeClasses.text} opacity-70`}>Developers and tech enthusiasts</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default NewsletterSubscription;

