import settingsService from '../../utils/settingsService';

const PortfolioHomeHero = () => {
  const [currentTheme, setCurrentTheme] = useState('cyberpunk');
  const [fontSize, setFontSize] = useState('medium');
  const [isThemeControlsOpen, setIsThemeControlsOpen] = useState(false);
  const [heroVideoUrl, setHeroVideoUrl] = useState('');
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [techStack, setTechStack] = useState([]);

  // Apply theme and font size to document
  useEffect(() => {
    const root = document.documentElement;

    // Apply theme classes
    root.className = `theme-${currentTheme}`;

    // Apply font size
    const fontSizeMap = {
      small: '14px',
      medium: '16px',
      large: '18px',
      xlarge: '20px'
    };
    root.style.fontSize = fontSizeMap[fontSize];
  }, [currentTheme, fontSize]);

  // Load settings (theme defaults and hero video)
  useEffect(() => {
    let mounted = true;
    (async () => {
      const res = await settingsService.getSettings();
      if (!mounted || !res.success) return;
      const stg = res.data || {};
      const ui = stg.ui_settings || {};
  if (stg.default_theme) setCurrentTheme(stg.default_theme);
  if (stg.default_font_size) setFontSize(stg.default_font_size);
  setVideoEnabled(stg.enable_video !== false);
  if (ui.hero_video_url) setHeroVideoUrl(ui.hero_video_url);
  if (Array.isArray(ui.tech_stack)) setTechStack(ui.tech_stack);
    })();
    return () => { mounted = false; };
  }, []);

  const handleThemeChange = (theme) => {
    setCurrentTheme(theme);
  };

  const handleFontSizeChange = (size) => {
    setFontSize(size);
  };

  const handleThemeControlsToggle = () => {
    setIsThemeControlsOpen(!isThemeControlsOpen);
  };

  const handleThemeControlsClose = () => {
    setIsThemeControlsOpen(false);
  };

  const getBackgroundClass = () => {
    switch(currentTheme) {
      case 'dark':
        return 'bg-gradient-to-br from-gray-900 via-gray-900 to-gray-800';
      case 'neural':
        return 'bg-gradient-to-br from-purple-900 via-purple-800 to-indigo-900';
      case 'futuristic':
        return 'bg-gradient-to-br from-slate-900 via-blue-900 to-indigo-900';
      case 'light':
        return 'bg-gradient-to-br from-gray-50 via-white to-blue-50';
      default: // cyberpunk
        return 'bg-gradient-to-br from-background via-surface to-background';
    }
  };

  return (
    <>
      <div className={`min-h-screen ${getBackgroundClass()} relative overflow-x-hidden`}>
        {/* Optional Hero Background Video */}
        {videoEnabled && heroVideoUrl && (
          <video
            className="pointer-events-none fixed inset-0 w-full h-full object-cover opacity-25"
            src={heroVideoUrl}
            autoPlay
            muted
            loop
            playsInline
          />
        )}
        {/* Header Navigation */}
        <HeaderNavigation 
          onThemeControlsToggle={handleThemeControlsToggle} 
          currentTheme={currentTheme}
        />

        {/* Theme Controls Modal */}
        <ThemeControls
          isOpen={isThemeControlsOpen}
          onClose={handleThemeControlsClose}
          currentTheme={currentTheme}
          onThemeChange={handleThemeChange}
          fontSize={fontSize}
          onFontSizeChange={handleFontSizeChange}
        />

        {/* Main Content */}
        <main className="relative z-10">
          {/* Hero Section */}
          <HeroSection currentTheme={currentTheme} />

          {/* Project Carousel */}
          <ProjectCarousel currentTheme={currentTheme} />

          {/* Project Showcase - Video/Image/Logo Projects */}
          <ProjectShowcase currentTheme={currentTheme} />

          {/* Technology Stack Grid */}
          <TechStackGrid currentTheme={currentTheme} items={techStack} />

          {/* Featured Blog Posts */}
          <FeaturedBlogPosts currentTheme={currentTheme} />

          {/* Newsletter Subscription */}
          <NewsletterSubscription currentTheme={currentTheme} />

          {/* Visitor Information Panel */}
          <VisitorInfoPanel currentTheme={currentTheme} />

          {/* Social Media Grid */}
          <SocialMediaGrid currentTheme={currentTheme} />
        </main>

        {/* Dynamic Footer */}
        <DynamicFooter currentTheme={currentTheme} />

        {/* Background Effects */}
        {currentTheme !== 'light' && (
          <div className="fixed inset-0 pointer-events-none overflow-hidden -z-10">
            <div className="absolute top-0 left-0 w-full h-full cyber-grid opacity-5"></div>
            <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-primary/10 rounded-full blur-3xl animate-pulse"></div>
            <div className="absolute bottom-1/4 right-1/4 w-80 h-80 bg-secondary/10 rounded-full blur-3xl animate-pulse delay-1000"></div>
            <div className="absolute top-3/4 left-1/2 w-64 h-64 bg-accent/10 rounded-full blur-3xl animate-pulse delay-2000"></div>
          </div>
        )}

        {/* Sticky Footer with Theme Controls Trigger */}
        <div className="fixed bottom-8 right-8 flex flex-col items-end space-y-3 z-40">
          {/* Theme Controls Trigger Button */}
          <button
            onClick={handleThemeControlsToggle}
            className={`
              w-12 h-12 rounded-full flex items-center justify-center transition-all duration-fast
              ${currentTheme === 'light' ?'bg-blue-600 text-white hover:bg-blue-700 shadow-lg' :'bg-primary text-background hover:bg-primary/90 glow-primary'
              }
              hover:scale-110 opacity-80 hover:opacity-100
            `}
            title="Open theme controls"
          >
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M12 3c.132 0 .263 0 .393 0a7.5 7.5 0 0 0 7.92 12.446a9 9 0 1 1-8.313-12.454z"
              />
            </svg>
          </button>

          {/* Scroll to Top Button */}
          <button
            onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            className={`
              w-12 h-12 rounded-full flex items-center justify-center transition-all duration-fast
              ${currentTheme === 'light' ?'bg-gray-600 text-white hover:bg-gray-700 shadow-lg' :'bg-surface text-primary hover:bg-surface/90 border border-primary/30'
              }
              hover:scale-110 opacity-80 hover:opacity-100
            `}
            aria-label="Scroll to top"
          >
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M5 10l7-7m0 0l7 7m-7-7v18"
              />
            </svg>
          </button>
        </div>
      </div>
    </>
  );
};

export default PortfolioHomeHero;

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import HeaderNavigation from '../../components/ui/HeaderNavigation';
import DynamicFooter from '../portfolio-home-hero/components/DynamicFooter';
import Icon from '../../components/AppIcon';
import Image from '../../components/AppImage';
import Button from '../../components/ui/Button';
import LogoCarousel from '../../components/ui/LogoCarousel';
import projectService from '../../utils/projectService';

const ProjectDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentTheme, setCurrentTheme] = useState('cyberpunk');

  useEffect(() => {
    const loadProject = async () => {
      try {
        setLoading(true);
        const res = await projectService.getProjectById(id);
        if (res.success) {
          setProject(res.data);
          // Increment view count
          await projectService.incrementViewCount(id);
        } else {
          setError('Project not found');
        }
      } catch (err) {
        setError('Failed to load project');
        console.error('Error loading project:', err);
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      loadProject();
    }
  }, [id]);

  useEffect(() => {
    // Get theme from localStorage
    const savedTheme = localStorage.getItem('theme') || 'cyberpunk';
    setCurrentTheme(savedTheme);
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen bg-background">
        <HeaderNavigation currentTheme={currentTheme} />
        <div className="flex items-center justify-center min-h-[60vh]">
          <div className="text-center">
            <div className="animate-spin w-8 h-8 border-2 border-primary border-t-transparent rounded-full mx-auto mb-4"></div>
            <p className="text-text-secondary">Loading project...</p>
          </div>
        </div>
        <DynamicFooter currentTheme={currentTheme} />
      </div>
    );
  }

  if (error || !project) {
    return (
      <div className="min-h-screen bg-background">
        <HeaderNavigation currentTheme={currentTheme} />
        <div className="flex items-center justify-center min-h-[60vh]">
          <div className="text-center">
            <Icon name="AlertCircle" size={48} className="text-error mx-auto mb-4" />
            <h1 className="text-2xl font-heading font-bold text-text-primary mb-2">Project Not Found</h1>
            <p className="text-text-secondary mb-6">{error}</p>
            <Button onClick={() => navigate('/projects-portfolio-grid')} variant="primary">
              Back to Projects
            </Button>
          </div>
        </div>
        <DynamicFooter currentTheme={currentTheme} />
      </div>
    );
  }

  const renderProjectContent = () => {
    switch (project.type) {
      case 'video':
        return (
          <div className="space-y-6">
            {project.video_url && (
              <div className="aspect-video rounded-lg overflow-hidden bg-surface/20">
                <video
                  src={project.video_url}
                  poster={project.video_poster}
                  controls
                  className="w-full h-full object-cover"
                >
                  Your browser does not support the video tag.
                </video>
              </div>
            )}
          </div>
        );
      case 'gallery':
        return (
          <div className="space-y-6">
            {project.gallery_images && project.gallery_images.length > 0 && (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {project.gallery_images.map((image, index) => (
                  <div key={index} className="aspect-square rounded-lg overflow-hidden bg-surface/20">
                    <Image
                      src={image}
                      alt={`${project.title} - Image ${index + 1}`}
                      className="w-full h-full object-cover hover:scale-105 transition-transform duration-300"
                    />
                  </div>
                ))}
              </div>
            )}
          </div>
        );
      case 'logo_gallery':
        return (
          <div className="space-y-6">
            <LogoCarousel images={project.gallery_images || []} />
            {project.gallery_images && project.gallery_images.length > 0 && (
              <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4 mt-6">
                {project.gallery_images.map((image, index) => (
                  <div key={index} className="aspect-square rounded-lg overflow-hidden bg-surface/20 p-4 flex items-center justify-center">
                    <Image
                      src={image}
                      alt={`Logo ${index + 1}`}
                      className="max-w-full max-h-full object-contain hover:scale-110 transition-transform duration-300"
                    />
                  </div>
                ))}
              </div>
            )}
          </div>
        );
      default:
        return (
          <div className="space-y-6">
            {project.featured_image && (
              <div className="aspect-video rounded-lg overflow-hidden bg-surface/20">
                <Image
                  src={project.featured_image}
                  alt={project.title}
                  className="w-full h-full object-cover"
                />
              </div>
            )}
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <Helmet>
        <title>{project.title} - Project Details</title>
        <meta name="description" content={project.description} />
      </Helmet>

      <HeaderNavigation currentTheme={currentTheme} />

      <main className="max-w-6xl mx-auto px-6 py-12">
        {/* Back button */}
        <div className="mb-8">
          <Button
            onClick={() => navigate('/projects-portfolio-grid')}
            variant="ghost"
            iconName="ArrowLeft"
            iconPosition="left"
            className="text-text-secondary hover:text-primary"
          >
            Back to Projects
          </Button>
        </div>

        {/* Project header */}
        <div className="mb-12">
          <div className="flex items-center gap-4 mb-4">
            <h1 className="text-4xl font-heading font-bold text-text-primary">
              {project.title}
            </h1>
            {project.featured && (
              <Icon name="Star" size={28} className="text-accent" />
            )}
            <div className={`px-3 py-1 rounded-full text-sm font-caption ${
              project.status === 'published' 
                ? 'bg-success/20 text-success border border-success/30' 
                : 'bg-warning/20 text-warning border border-warning/30'
            }`}>
              {project.status.toUpperCase()}
            </div>
            <div className={`px-3 py-1 rounded-full text-sm font-caption bg-primary/20 text-primary border border-primary/30`}>
              {project.type?.replace('_', ' ').toUpperCase() || 'PROJECT'}
            </div>
          </div>

          {project.description && (
            <p className="text-lg text-text-secondary leading-relaxed max-w-4xl">
              {project.description}
            </p>
          )}
        </div>

        {/* Project content */}
        <div className="mb-12">
          {renderProjectContent()}
        </div>

        {/* Project details */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-12">
          {/* Main content */}
          <div className="lg:col-span-2 space-y-8">
            {project.content && (
              <div>
                <h2 className="text-2xl font-heading font-semibold text-text-primary mb-4">
                  Project Overview
                </h2>
                <div className="prose prose-lg text-text-secondary">
                  {project.content}
                </div>
              </div>
            )}
          </div>

          {/* Sidebar */}
          <div className="space-y-8">
            {/* Technologies */}
            {project.tags && project.tags.length > 0 && (
              <div>
                <h3 className="text-lg font-heading font-semibold text-text-primary mb-4">
                  Technologies
                </h3>
                <div className="flex flex-wrap gap-2">
                  {project.tags.map((tech, index) => (
                    <span
                      key={index}
                      className="px-3 py-1 bg-primary/10 text-primary rounded-full text-sm font-caption border border-primary/20"
                    >
                      {tech}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Links */}
            <div className="space-y-4">
              {project.github_url && (
                <a
                  href={project.github_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="flex items-center gap-3 p-4 bg-surface/20 rounded-lg border border-border-accent/20 hover:border-primary/30 transition-colors"
                >
                  <Icon name="Github" size={20} className="text-text-secondary" />
                  <span className="text-text-primary font-medium">View Source Code</span>
                  <Icon name="ExternalLink" size={16} className="text-text-secondary ml-auto" />
                </a>
              )}
              {project.live_url && (
                <a
                  href={project.live_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="flex items-center gap-3 p-4 bg-surface/20 rounded-lg border border-border-accent/20 hover:border-primary/30 transition-colors"
                >
                  <Icon name="ExternalLink" size={20} className="text-text-secondary" />
                  <span className="text-text-primary font-medium">View Live Project</span>
                  <Icon name="ExternalLink" size={16} className="text-text-secondary ml-auto" />
                </a>
              )}
            </div>

            {/* Project stats */}
            <div className="bg-surface/20 rounded-lg p-6 border border-border-accent/20">
              <h3 className="text-lg font-heading font-semibold text-text-primary mb-4">
                Project Stats
              </h3>
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-text-secondary">Views</span>
                  <span className="text-text-primary font-medium">{project.view_count || 0}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-text-secondary">Likes</span>
                  <span className="text-text-primary font-medium">{project.like_count || 0}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-text-secondary">Created</span>
                  <span className="text-text-primary font-medium">
                    {new Date(project.created_at).toLocaleDateString()}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </main>

      <DynamicFooter currentTheme={currentTheme} />
    </div>
  );
};

export default ProjectDetail;

