    <div className="group relative bg-surface border border-border-accent/20 rounded-lg overflow-hidden hover:border-primary/40 transition-all duration-normal hover-glow-primary">
      {/* Project Link */}
      <Link to={`/project/${project.id}`} className="block">
        {/* Project Image */}
        <div className="relative h-48 overflow-hidden">
          <Image
            src={project.image}
            alt={project.title}
            className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-slow"
          />
          <div className="absolute inset-0 bg-gradient-to-t from-background/80 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-normal"></div>

          {/* Status Badge */}
          {project.status && (
            <div className={`absolute top-3 right-3 px-2 py-1 rounded-full text-xs font-caption ${
              project.status === 'completed' 
                ? 'bg-success/20 text-success border border-success/30' 
                : project.status === 'in-progress' ?'bg-warning/20 text-warning border border-warning/30' :'bg-primary/20 text-primary border border-primary/30'
            }`}>
              {project.status.replace('-', ' ').toUpperCase()}
            </div>
          )}
        </div>

        {/* Project Content */}
        <div className="p-6">
          <div className="flex items-start justify-between mb-3">
            <h3 className="font-heading font-semibold text-lg text-text-primary group-hover:text-primary transition-colors duration-fast line-clamp-2">
              {project.title}
            </h3>
            {project.featured && (
              <Icon name="Star" size={16} className="text-accent flex-shrink-0 ml-2" />
            )}
          </div>

          <p className="text-text-secondary text-sm mb-4 line-clamp-3 leading-relaxed">
            {project.description}
          </p>

          {/* Technologies */}
          <div className="flex flex-wrap gap-1 mb-4">
            {(project.technologies || []).slice(0, 3).map((tech, index) => (
              <span
                key={index}
                className="px-2 py-1 bg-primary/10 text-primary text-xs rounded-full font-caption border border-primary/20"
              >
                {tech}
              </span>
            ))}
            {(project.technologies || []).length > 3 && (
              <span className="px-2 py-1 bg-surface text-text-secondary text-xs rounded-full font-caption">
                +{project.technologies.length - 3}
              </span>
            )}
          </div>

          {/* Project Links */}
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              {project.liveUrl && (
                <a
                  href={project.liveUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="action-button flex items-center space-x-1 text-text-secondary hover:text-primary transition-colors duration-fast"
                  onClick={(e) => e.stopPropagation()}
                >
                  <Icon name="ExternalLink" size={14} />
                  <span className="text-xs font-caption">Live</span>
                </a>
              )}
              {project.githubUrl && (
                <a
                  href={project.githubUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="action-button flex items-center space-x-1 text-text-secondary hover:text-primary transition-colors duration-fast"
                  onClick={(e) => e.stopPropagation()}
                >
                  <Icon name="Github" size={14} />
                  <span className="text-xs font-caption">Code</span>
                </a>
              )}
            </div>

            <div className="text-xs text-text-secondary font-caption">
              {project.completedDate}
            </div>
          </div>
        </div>
      </Link>

      {/* Admin Actions */}
      {isAdmin && (
        <div className="flex items-center justify-end space-x-2 mt-4 pt-4 border-t border-border-accent/20 px-6 pb-6">
          <Button
            variant="ghost"
            size="xs"
            iconName="Edit"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              onEdit(project);
            }}
            className="action-button text-warning hover:text-warning"
          >
            Edit
          </Button>
          <Button
            variant="ghost"
            size="xs"
            iconName="Trash2"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              onDelete(project);
            }}
            className="action-button text-error hover:text-error"
          >
            Delete
          </Button>
        </div>
      )}

      {/* Hover Overlay */}
      <div className="absolute inset-0 bg-primary/5 opacity-0 group-hover:opacity-100 transition-opacity duration-normal pointer-events-none"></div>
    </div>
  );
};

export default ProjectCard;

import React from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';

const ProjectFilters = ({ 
  searchTerm, 
  onSearchChange, 
  selectedTechnology, 
  onTechnologyChange, 
  selectedStatus, 
  onStatusChange, 
  sortBy, 
  onSortChange, 
  onClearFilters,
  technologies,
  totalProjects,
  filteredCount 
}) => {
  const statusOptions = [
    { value: '', label: 'All Status' },
    { value: 'completed', label: 'Completed' },
    { value: 'in-progress', label: 'In Progress' },
    { value: 'planning', label: 'Planning' }
  ];

  const sortOptions = [
    { value: 'newest', label: 'Newest First' },
    { value: 'oldest', label: 'Oldest First' },
    { value: 'title', label: 'Title A-Z' },
    { value: 'featured', label: 'Featured First' }
  ];

  const hasActiveFilters = searchTerm || selectedTechnology || selectedStatus || sortBy !== 'newest';

  return (
    <div className="bg-surface/50 backdrop-blur-sm border border-border-accent/20 rounded-lg p-6 mb-8">
      {/* Search Bar */}
      <div className="relative mb-6">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <Icon name="Search" size={18} className="text-text-secondary" />
        </div>
        <Input
          type="search"
          placeholder="Search projects by title, description, or technology..."
          value={searchTerm}
          onChange={(e) => onSearchChange(e.target.value)}
          className="pl-10 pr-4 py-3 w-full bg-background/50 border-border-accent/30 focus:border-primary/50 rounded-lg"
        />
        {searchTerm && (
          <button
            onClick={() => onSearchChange('')}
            className="absolute inset-y-0 right-0 pr-3 flex items-center text-text-secondary hover:text-primary transition-colors duration-fast"
          >
            <Icon name="X" size={18} />
          </button>
        )}
      </div>

      {/* Filter Controls */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        {/* Technology Filter */}
        <div>
          <label className="block text-sm font-medium text-text-primary mb-2">
            Technology
          </label>
          <select
            value={selectedTechnology}
            onChange={(e) => onTechnologyChange(e.target.value)}
            className="w-full px-3 py-2 bg-background/50 border border-border-accent/30 rounded-lg text-text-primary focus:border-primary/50 focus:outline-none transition-colors duration-fast"
          >
            <option value="">All Technologies</option>
            {technologies.map((tech) => (
              <option key={tech} value={tech}>
                {tech}
              </option>
            ))}
          </select>
        </div>

        {/* Status Filter */}
        <div>
          <label className="block text-sm font-medium text-text-primary mb-2">
            Status
          </label>
          <select
            value={selectedStatus}
            onChange={(e) => onStatusChange(e.target.value)}
            className="w-full px-3 py-2 bg-background/50 border border-border-accent/30 rounded-lg text-text-primary focus:border-primary/50 focus:outline-none transition-colors duration-fast"
          >
            {statusOptions.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>

        {/* Sort By */}
        <div>
          <label className="block text-sm font-medium text-text-primary mb-2">
            Sort By
          </label>
          <select
            value={sortBy}
            onChange={(e) => onSortChange(e.target.value)}
            className="w-full px-3 py-2 bg-background/50 border border-border-accent/30 rounded-lg text-text-primary focus:border-primary/50 focus:outline-none transition-colors duration-fast"
          >
            {sortOptions.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>

        {/* Clear Filters */}
        <div className="flex items-end">
          <Button
            variant="outline"
            size="sm"
            iconName="RotateCcw"
            onClick={onClearFilters}
            disabled={!hasActiveFilters}
            className="w-full"
          >
            Clear Filters
          </Button>
        </div>
      </div>

      {/* Results Summary */}
      <div className="flex items-center justify-between text-sm">
        <div className="text-text-secondary font-caption">
          Showing {filteredCount} of {totalProjects} projects
          {hasActiveFilters && (
            <span className="text-primary ml-1">(filtered)</span>
          )}
        </div>

        {hasActiveFilters && (
          <div className="flex items-center space-x-2">
            <Icon name="Filter" size={14} className="text-primary" />
            <span className="text-primary text-xs font-caption">
              Active filters applied
            </span>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProjectFilters;

import React, { useState, useEffect } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';

const ProjectForm = ({ project, isOpen, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    fullDescription: '',
    image: '',
    technologies: [],
    status: 'planning',
    featured: false,
    liveUrl: '',
    githubUrl: '',
    completedDate: '',
    duration: '',
    client: '',
    features: [],
    challenges: ''
  });

  const [techInput, setTechInput] = useState('');
  const [featureInput, setFeatureInput] = useState('');
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (project) {
      setFormData({
        ...project,
        technologies: project.technologies || [],
        features: project.features || []
      });
    } else {
      setFormData({
        title: '',
        description: '',
        fullDescription: '',
        image: '',
        technologies: [],
        status: 'planning',
        featured: false,
        liveUrl: '',
        githubUrl: '',
        completedDate: '',
        duration: '',
        client: '',
        features: [],
        challenges: ''
      });
    }
    setErrors({});
  }, [project, isOpen]);

  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = 'unset';
    };
  }, [isOpen, onClose]);

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));

    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: ''
      }));
    }
  };

  const addTechnology = () => {
    if (techInput.trim() && !formData.technologies.includes(techInput.trim())) {
      setFormData(prev => ({
        ...prev,
        technologies: [...prev.technologies, techInput.trim()]
      }));
      setTechInput('');
    }
  };

  const removeTechnology = (tech) => {
    setFormData(prev => ({
      ...prev,
      technologies: prev.technologies.filter(t => t !== tech)
    }));
  };

  const addFeature = () => {
    if (featureInput.trim() && !formData.features.includes(featureInput.trim())) {
      setFormData(prev => ({
        ...prev,
        features: [...prev.features, featureInput.trim()]
      }));
      setFeatureInput('');
    }
  };

  const removeFeature = (feature) => {
    setFormData(prev => ({
      ...prev,
      features: prev.features.filter(f => f !== feature)
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }

    if (!formData.description.trim()) {
      newErrors.description = 'Description is required';
    }

    if (!formData.image.trim()) {
      newErrors.image = 'Image URL is required';
    }

    if (formData.technologies.length === 0) {
      newErrors.technologies = 'At least one technology is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      const projectData = {
        ...formData,
        id: project?.id || Date.now().toString(),
        updatedAt: new Date().toISOString()
      };

      onSave(projectData);
      onClose();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
      {/* Backdrop */}
      <div 
        className="absolute inset-0 bg-background/80 backdrop-blur-sm"
        onClick={onClose}
      ></div>

      {/* Modal Content */}
      <div className="relative bg-surface border border-border-accent/30 rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto shadow-glow-primary">
        {/* Header */}
        <div className="sticky top-0 bg-surface/95 backdrop-blur-sm border-b border-border-accent/20 p-6 flex items-center justify-between">
          <h2 className="font-heading font-bold text-xl text-text-primary">
            {project ? 'Edit Project' : 'Add New Project'}
          </h2>
          <Button
            variant="ghost"
            size="sm"
            iconName="X"
            onClick={onClose}
            className="text-text-secondary hover:text-primary"
          />
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {/* Basic Information */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-text-primary mb-2">
                Project Title *
              </label>
              <Input
                type="text"
                value={formData.title}
                onChange={(e) => handleInputChange('title', e.target.value)}
                placeholder="Enter project title"
                className={errors.title ? 'border-error' : ''}
              />
              {errors.title && (
                <p className="text-error text-xs mt-1">{errors.title}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-2">
                Image URL *
              </label>
              <Input
                type="url"
                value={formData.image}
                onChange={(e) => handleInputChange('image', e.target.value)}
                placeholder="https://example.com/image.jpg"
                className={errors.image ? 'border-error' : ''}
              />
              {errors.image && (
                <p className="text-error text-xs mt-1">{errors.image}</p>
              )}
            </div>
          </div>

          {/* Description */}
          <div>
            <label className="block text-sm font-medium text-text-primary mb-2">
              Short Description *
            </label>
            <textarea
              value={formData.description}
              onChange={(e) => handleInputChange('description', e.target.value)}
              placeholder="Brief description for project cards"
              rows={3}
              className={`w-full px-3 py-2 bg-background/50 border rounded-lg text-text-primary focus:border-primary/50 focus:outline-none transition-colors duration-fast resize-none ${
                errors.description ? 'border-error' : 'border-border-accent/30'
              }`}
            />
            {errors.description && (
              <p className="text-error text-xs mt-1">{errors.description}</p>
            )}
          </div>

          {/* Full Description */}
          <div>
            <label className="block text-sm font-medium text-text-primary mb-2">
              Full Description
            </label>
            <textarea
              value={formData.fullDescription}
              onChange={(e) => handleInputChange('fullDescription', e.target.value)}
              placeholder="Detailed project description for modal view"
              rows={4}
              className="w-full px-3 py-2 bg-background/50 border border-border-accent/30 rounded-lg text-text-primary focus:border-primary/50 focus:outline-none transition-colors duration-fast resize-none"
            />
          </div>

          {/* Technologies */}
          <div>
            <label className="block text-sm font-medium text-text-primary mb-2">
              Technologies *
            </label>
            <div className="flex space-x-2 mb-3">
              <Input
                type="text"
                value={techInput}
                onChange={(e) => setTechInput(e.target.value)}
                placeholder="Add technology"
                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addTechnology())}
                className="flex-1"
              />
              <Button
                type="button"
                variant="outline"
                size="sm"
                iconName="Plus"
                onClick={addTechnology}
              >
                Add
              </Button>
            </div>
            <div className="flex flex-wrap gap-2">
              {formData.technologies.map((tech, index) => (
                <span
                  key={index}
                  className="flex items-center space-x-1 px-3 py-1 bg-primary/10 text-primary text-sm rounded-md border border-primary/20"
                >
                  <span>{tech}</span>
                  <button
                    type="button"
                    onClick={() => removeTechnology(tech)}
                    className="text-primary hover:text-error transition-colors duration-fast"
                  >
                    <Icon name="X" size={14} />
                  </button>
                </span>
              ))}
            </div>
            {errors.technologies && (
              <p className="text-error text-xs mt-1">{errors.technologies}</p>
            )}
          </div>

          {/* Project Details */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <div>
              <label className="block text-sm font-medium text-text-primary mb-2">
                Status
              </label>
              <select
                value={formData.status}
                onChange={(e) => handleInputChange('status', e.target.value)}
                className="w-full px-3 py-2 bg-background/50 border border-border-accent/30 rounded-lg text-text-primary focus:border-primary/50 focus:outline-none transition-colors duration-fast"
              >
                <option value="planning">Planning</option>
                <option value="in-progress">In Progress</option>
                <option value="completed">Completed</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-2">
                Completed Date
              </label>
              <Input
                type="date"
                value={formData.completedDate}
                onChange={(e) => handleInputChange('completedDate', e.target.value)}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-2">
                Duration
              </label>
              <Input
                type="text"
                value={formData.duration}
                onChange={(e) => handleInputChange('duration', e.target.value)}
                placeholder="e.g., 3 months"
              />
            </div>
          </div>

          {/* URLs and Client */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <div>
              <label className="block text-sm font-medium text-text-primary mb-2">
                Live URL
              </label>
              <Input
                type="url"
                value={formData.liveUrl}
                onChange={(e) => handleInputChange('liveUrl', e.target.value)}
                placeholder="https://project-demo.com"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-2">
                GitHub URL
              </label>
              <Input
                type="url"
                value={formData.githubUrl}
                onChange={(e) => handleInputChange('githubUrl', e.target.value)}
                placeholder="https://github.com/username/repo"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-2">
                Client
              </label>
              <Input
                type="text"
                value={formData.client}
                onChange={(e) => handleInputChange('client', e.target.value)}
                placeholder="Client name or 'Personal'"
              />
            </div>
          </div>

          {/* Features */}
          <div>
            <label className="block text-sm font-medium text-text-primary mb-2">
              Key Features
            </label>
            <div className="flex space-x-2 mb-3">
              <Input
                type="text"
                value={featureInput}
                onChange={(e) => setFeatureInput(e.target.value)}
                placeholder="Add key feature"
                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addFeature())}
                className="flex-1"
              />
              <Button
                type="button"
                variant="outline"
                size="sm"
                iconName="Plus"
                onClick={addFeature}
              >
                Add
              </Button>
            </div>
            <div className="space-y-2">
              {formData.features.map((feature, index) => (
                <div
                  key={index}
                  className="flex items-center justify-between p-2 bg-background/50 border border-border-accent/20 rounded-lg"
                >
                  <span className="text-text-primary text-sm">{feature}</span>
                  <button
                    type="button"
                    onClick={() => removeFeature(feature)}
                    className="text-text-secondary hover:text-error transition-colors duration-fast"
                  >
                    <Icon name="X" size={16} />
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* Challenges */}
          <div>
            <label className="block text-sm font-medium text-text-primary mb-2">
              Technical Challenges
            </label>
            <textarea
              value={formData.challenges}
              onChange={(e) => handleInputChange('challenges', e.target.value)}
              placeholder="Describe technical challenges faced and how they were solved"
              rows={3}
              className="w-full px-3 py-2 bg-background/50 border border-border-accent/30 rounded-lg text-text-primary focus:border-primary/50 focus:outline-none transition-colors duration-fast resize-none"
            />
          </div>

          {/* Featured Toggle */}
          <div className="flex items-center space-x-3">
            <input
              type="checkbox"
              id="featured"
              checked={formData.featured}
              onChange={(e) => handleInputChange('featured', e.target.checked)}
              className="w-4 h-4 text-primary bg-background border-border-accent/30 rounded focus:ring-primary/50"
            />
            <label htmlFor="featured" className="text-sm font-medium text-text-primary">
              Mark as featured project
            </label>
          </div>

          {/* Form Actions */}
          <div className="flex items-center justify-end space-x-4 pt-6 border-t border-border-accent/20">
            <Button
              type="button"
              variant="ghost"
              onClick={onClose}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              variant="primary"
              iconName="Save"
            >
              {project ? 'Update Project' : 'Create Project'}
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectForm;

import React, { useEffect } from 'react';
import Image from '../../../components/AppImage';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';

