import React, { useState, useEffect, useMemo } from 'react';
import { Helmet } from 'react-helmet';
import Icon from '../../components/AppIcon';
import Button from '../../components/ui/Button';
import ProjectCard from './components/ProjectCard';
import ProjectFilters from './components/ProjectFilters';
import ProjectModal from './components/ProjectModal';
import ProjectForm from './components/ProjectForm';
import ProjectSkeleton from './components/ProjectSkeleton';
import projectService from '../../utils/projectService';

const ProjectsPortfolioGrid = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTechnology, setSelectedTechnology] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('');
  const [sortBy, setSortBy] = useState('newest');
  const [selectedProject, setSelectedProject] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editingProject, setEditingProject] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  // Load projects from Supabase
  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        setLoading(true);
        const res = await projectService.getPublishedProjects();
        if (!mounted) return;
        if (res.success) {
          const normalized = (res.data || []).map((p) => ({
            id: p.id,
            title: p.title,
            description: p.description,
            fullDescription: p.content || p.description,
            image: p.featured_image || '/assets/images/no_image.png',
            technologies: p.technologies || [],
            status: p.status === 'published' ? 'completed' : p.status,
            featured: !!p.featured,
            liveUrl: p.live_url || '',
            githubUrl: p.github_url || '',
            completedDate: (p.updated_at || p.created_at || '').slice(0, 10),
            duration: '',
            client: 'Personal',
            features: [],
            challenges: ''
          }));
          setProjects(normalized);
        } else {
          setProjects([]);
        }
      } catch (e) {
        setProjects([]);
      } finally {
        if (mounted) setLoading(false);
      }
    })();
    return () => { mounted = false; };
  }, []);

  // Check admin status (mock implementation)
  useEffect(() => {
    const adminStatus = localStorage.getItem('isAdmin') === 'true';
    setIsAdmin(adminStatus);
  }, []);

  // Get unique technologies for filter
  const allTechnologies = useMemo(() => {
    const techs = new Set();
    projects.forEach(project => {
      project.technologies.forEach(tech => techs.add(tech));
    });
    return Array.from(techs).sort();
  }, [projects]);

  // Filter and sort projects
  const filteredProjects = useMemo(() => {
    let filtered = projects.filter(project => {
      const matchesSearch = !searchTerm || 
        project.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        project.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        project.technologies.some(tech => tech.toLowerCase().includes(searchTerm.toLowerCase()));

      const matchesTechnology = !selectedTechnology || 
        project.technologies.includes(selectedTechnology);

      const matchesStatus = !selectedStatus || project.status === selectedStatus;

      return matchesSearch && matchesTechnology && matchesStatus;
    });

    // Sort projects
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'oldest':
          return new Date(a.completedDate || '1970-01-01') - new Date(b.completedDate || '1970-01-01');
        case 'title':
          return a.title.localeCompare(b.title);
        case 'featured':
          if (a.featured && !b.featured) return -1;
          if (!a.featured && b.featured) return 1;
          return new Date(b.completedDate || '1970-01-01') - new Date(a.completedDate || '1970-01-01');
        case 'newest':
        default:
          return new Date(b.completedDate || '1970-01-01') - new Date(a.completedDate || '1970-01-01');
      }
    });

    return filtered;
  }, [projects, searchTerm, selectedTechnology, selectedStatus, sortBy]);

  const handleClearFilters = () => {
    setSearchTerm('');
    setSelectedTechnology('');
    setSelectedStatus('');
    setSortBy('newest');
  };

  const handleViewDetails = (project) => {
    setSelectedProject(project);
    setIsModalOpen(true);
  };

  const handleEditProject = (project) => {
    setEditingProject(project);
    setIsFormOpen(true);
  };

  const handleDeleteProject = (project) => {
    if (window.confirm(`Are you sure you want to delete "${project.title}"?`)) {
      setProjects(prev => prev.filter(p => p.id !== project.id));
    }
  };

  const handleSaveProject = (projectData) => {
    if (editingProject) {
      // Update existing project
      setProjects(prev => prev.map(p => p.id === projectData.id ? projectData : p));
    } else {
      // Add new project
      setProjects(prev => [projectData, ...prev]);
    }
    setEditingProject(null);
  };

  const handleAddProject = () => {
    setEditingProject(null);
    setIsFormOpen(true);
  };

  return (
    <>
      <Helmet>
        <title>Mikael Kraft  Projects</title>
        <meta name="description" content="Explore Mikael Kraft's complete portfolio of web development, blockchain, and machine learning projects. View live demos, source code, and technical details." />
        <meta name="keywords" content="portfolio, projects, web development, React, blockchain, machine learning, cyberpunk" />
        <meta property="og:title" content="Mikael Kraft  Projects" />
        <meta property="og:description" content="A curated portfolio of software engineering projects by Mikael Kraft." />
        <meta property="og:type" content="website" />
        <meta property="og:url" content="https://mkraft.tech/projects" />
        <meta property="og:image" content="/assets/images/no_image.png" />
        <meta name="twitter:card" content="summary_large_image" />
        <meta name="twitter:site" content="@mikael_kraft" />
        <meta name="twitter:creator" content="@mikael_kraft" />
        <link rel="canonical" href="https://mkraft.tech/projects" />
      </Helmet>

      <div className="min-h-screen bg-background pt-20 pb-16">
        <div className="max-w-9xl mx-auto px-6 lg:px-8">
          {/* Page Header */}
          <div className="text-center mb-12">
            <div className="inline-flex items-center space-x-2 px-4 py-2 bg-primary/10 border border-primary/20 rounded-full mb-6">
              <Icon name="FolderOpen" size={18} className="text-primary" />
              <span className="text-primary font-caption text-sm">Portfolio Showcase</span>
            </div>

            <h1 className="font-heading font-bold text-4xl md:text-5xl lg:text-6xl text-text-primary mb-6">
              Project <span className="text-primary">Portfolio</span>
            </h1>

            <p className="text-text-secondary text-lg md:text-xl max-w-3xl mx-auto leading-relaxed">
              Explore my collection of innovative projects spanning web development, blockchain technology, 
              machine learning, and cyberpunk-themed applications. Each project represents a unique challenge 
              and creative solution.
            </p>
          </div>

          {/* Filters */}
          <ProjectFilters
            searchTerm={searchTerm}
            onSearchChange={setSearchTerm}
            selectedTechnology={selectedTechnology}
            onTechnologyChange={setSelectedTechnology}
            selectedStatus={selectedStatus}
            onStatusChange={setSelectedStatus}
            sortBy={sortBy}
            onSortChange={setSortBy}
            onClearFilters={handleClearFilters}
            technologies={allTechnologies}
            totalProjects={projects.length}
            filteredCount={filteredProjects.length}
          />

          {/* Projects Grid */}
          {loading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {Array.from({ length: 6 }).map((_, index) => (
                <ProjectSkeleton key={index} />
              ))}
            </div>
          ) : filteredProjects.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {filteredProjects.map((project) => (
                <ProjectCard
                  key={project.id}
                  project={project}
                  isAdmin={isAdmin}
                  onEdit={handleEditProject}
                  onDelete={handleDeleteProject}
                  onViewDetails={handleViewDetails}
                />
              ))}
            </div>
          ) : (
            <div className="text-center py-16">
              <div className="w-24 h-24 mx-auto mb-6 bg-surface rounded-full flex items-center justify-center">
                <Icon name="Search" size={32} className="text-text-secondary" />
              </div>
              <h3 className="font-heading font-semibold text-xl text-text-primary mb-2">
                No Projects Found
              </h3>
              <p className="text-text-secondary mb-6">
                Try adjusting your search criteria or filters to find more projects.
              </p>
              <Button
                variant="outline"
                iconName="RotateCcw"
                onClick={handleClearFilters}
              >
                Clear All Filters
              </Button>
            </div>
          )}

          {/* Admin Floating Action Button */}
          {isAdmin && (
            <div className="fixed bottom-8 right-8 z-40">
              <Button
                variant="primary"
                size="lg"
                iconName="Plus"
                onClick={handleAddProject}
                className="rounded-full w-14 h-14 shadow-glow-primary hover:scale-110 transition-transform duration-fast"
                title="Add New Project"
              />
            </div>
          )}
        </div>

        {/* Project Detail Modal */}
        <ProjectModal
          project={selectedProject}
          isOpen={isModalOpen}
          onClose={() => {
            setIsModalOpen(false);
            setSelectedProject(null);
          }}
        />

        {/* Project Form Modal */}
        <ProjectForm
          project={editingProject}
          isOpen={isFormOpen}
          onClose={() => {
            setIsFormOpen(false);
            setEditingProject(null);
          }}
          onSave={handleSaveProject}
        />
      </div>
    </>
  );
};

export default ProjectsPortfolioGrid;

import React, { useEffect, useState } from 'react';
import { useSearchParams, Link } from 'react-router-dom';
import blogService from 'utils/blogService';
import HeaderNavigation from 'components/ui/HeaderNavigation';
import useFeature from 'hooks/useFeature';
import Icon from 'components/AppIcon';

export default function SearchResultsPage() {
  const [params] = useSearchParams();
  const q = params.get('q') || '';
  const enabled = useFeature('full_text_search', true);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    let active = true;
    if (!enabled || !q.trim()) { setResults([]); return; }
    setLoading(true); setError(null);
    (async () => {
      const res = await blogService.searchPosts(q.trim());
      if (!active) return;
      if (res.success) setResults(res.data);
      else setError(res.error || 'Search failed');
      setLoading(false);
    })();
    return () => { active = false; };
  }, [q, enabled]);

  return (
    <div className="min-h-screen bg-background">
      <HeaderNavigation />
      <main className="max-w-4xl mx-auto px-6 pt-28 pb-16">
        <h1 className="text-2xl font-semibold mb-6 flex items-center gap-2"><Icon name="Search" size={22}/> Search</h1>
        {!enabled && <p className="text-text-secondary">Search is currently disabled.</p>}
        {enabled && (
          <>
            <p className="text-sm text-text-secondary mb-4">Query: <span className="font-mono text-text-primary">{q || ''}</span></p>
            {loading && <p className="text-sm text-text-secondary">Searching</p>}
            {error && <p className="text-error text-sm">{error}</p>}
            {!loading && !error && enabled && q && results.length === 0 && (
              <p className="text-sm text-text-secondary">No results found.</p>
            )}
            <ul className="space-y-4">
              {results.map(r => {
                const snippetHtml = r.snippet || '';
                const hasSnippet = /<mark>/i.test(snippetHtml);
                return (
                  <li key={r.id} className="p-4 rounded border border-border-accent/20 bg-surface hover:border-primary/60 transition">
                    <Link to={`/blog/${r.slug}`} className="group block">
                      <h2 className="font-medium group-hover:text-primary line-clamp-1">{r.title}</h2>
                      {hasSnippet && (
                        <p className="text-sm text-text-secondary mt-1 line-clamp-3" dangerouslySetInnerHTML={{ __html: snippetHtml }} />
                      )}
                      {!hasSnippet && r.excerpt && (
                        <p className="text-sm text-text-secondary line-clamp-2 mt-1">{r.excerpt}</p>
                      )}
                      <div className="mt-2 text-[10px] uppercase tracking-wide text-text-secondary/70 flex items-center gap-2">
                        {r.published_at && <time dateTime={r.published_at}>{new Date(r.published_at).toLocaleDateString()}</time>}
                        {typeof r.rank === 'number' && <span>Rank: {r.rank.toFixed(2)}</span>}
                      </div>
                    </Link>
                  </li>
                );
              })}
            </ul>
          </>
        )}
      </main>
    </div>
  );
}

// Minimal AdSense script loader to ensure we only inject once per publisher
let loadedClientId = null;
let loadPromise = null;

export function ensureAdSenseLoaded(publisherId) {
  if (!publisherId || typeof document === 'undefined') return Promise.resolve(false);
  const clientParam = `ca-pub-${publisherId.replace('ca-pub-', '')}`;

  // If already loaded for this client, resolve
  if (loadedClientId === clientParam && window.adsbygoogle) return Promise.resolve(true);

  // If a script with this client already exists in DOM
  const existing = Array.from(document.querySelectorAll('script[src*="pagead2.googlesyndication.com/pagead/js/adsbygoogle.js"]'))
    .find(s => s.src.includes(`client=${clientParam}`));
  if (existing) {
    loadedClientId = clientParam;
    return Promise.resolve(true);
  }

  if (loadPromise) return loadPromise;

  loadPromise = new Promise(resolve => {
    const script = document.createElement('script');
    script.async = true;
    script.src = `https://pagead2.googlesyndication.com/pagead/js/adsbygoogle.js?client=${clientParam}`;
    script.crossOrigin = 'anonymous';
    script.onload = () => {
      loadedClientId = clientParam;
      resolve(true);
    };
    script.onerror = () => resolve(false);
    document.head.appendChild(script);
  });

  return loadPromise;
}

export function pushAd() {
  try {
    if (typeof window !== 'undefined' && window.adsbygoogle) {
      window.adsbygoogle.push({});
      return true;
    }
  } catch (_) {}
  return false;
}

import supabase from './supabase';

export const ADMIN_EMAIL = 'mikewillkraft@gmail.com';

class AuthService {
  // Detect local/dev environment (Vite)
  isDevEnv() {
    try {
      const viteDev = typeof import.meta !== 'undefined' && import.meta && import.meta.env && !!import.meta.env.DEV;
      const isLocalhost = typeof window !== 'undefined' && ['localhost', '127.0.0.1'].includes(window.location.hostname);
      return !!(viteDev || isLocalhost);
    } catch (_) {
      return false;
    }
  }
  // Sign in with email and password
  async signIn(email, password) {
    try {
      // Restrict sign-in to admin email only
      if (!email || email.toLowerCase() !== ADMIN_EMAIL.toLowerCase()) {
        return { success: false, error: 'Access denied. This portal is for the admin only.' };
      }
      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password
      });

      if (error) {
        return { success: false, error: error.message };
      }

      // Ensure admin profile exists/updated
      try {
        if (data?.user?.id) {
          await this.ensureAdminProfile(data.user.id, email);
        }
  } catch {}

      return { success: true, data };
    } catch (error) {
      if (error?.message?.includes('Failed to fetch') || 
          error?.message?.includes('AuthRetryableFetchError')) {
        return { 
          success: false, 
          error: 'Cannot connect to authentication service. Your Supabase project may be paused or inactive. Please check your Supabase dashboard and resume your project if needed.' 
        };
      }
      return { success: false, error: 'An unexpected error occurred during sign in' };
    }
  }

  // Sign in with OAuth (Google/GitHub)
  async signInWithOAuth(provider) {
    try {
      const { data, error } = await supabase.auth.signInWithOAuth({
        provider,
        options: {
          redirectTo: `${window.location.origin}/admin-dashboard-content-management`,
          queryParams: {
            access_type: 'offline',
            prompt: 'consent',
          }
        }
      });

      if (error) {
        return { success: false, error: error.message };
      }

      return { success: true, data };
    } catch (error) {
      if (error?.message?.includes('Failed to fetch') || 
          error?.message?.includes('AuthRetryableFetchError')) {
        return { 
          success: false, 
          error: 'Cannot connect to authentication service. Your Supabase project may be paused or inactive. Please check your Supabase dashboard and resume your project if needed.' 
        };
      }
      return { success: false, error: 'An unexpected error occurred during OAuth sign in' };
    }
  }

  // Sign up new user
  async signUp(email, password, userData = {}) {
    // Public signup is intentionally disabled; keep simple deterministic response
    return { success: false, error: 'Signup is disabled. Please contact the site owner.' };
  }

  // Sign out
  async signOut() {
    try {
      const { error } = await supabase.auth.signOut();

      if (error) {
        return { success: false, error: error.message };
      }

      // Dev-only cleanup
      try {
        if (typeof window !== 'undefined') {
          window.localStorage.removeItem('dev_admin');
          window.localStorage.removeItem('dev_admin_email');
          window.localStorage.removeItem('dev_admin_code');
        }
      } catch (_) {}

      return { success: true };
    } catch (error) {
      if (error?.message?.includes('Failed to fetch') || 
          error?.message?.includes('NetworkError') ||
          error?.name === 'TypeError' && error?.message?.includes('fetch')) {
        return { 
          success: false, 
          error: 'Cannot connect to authentication service. Your Supabase project may be paused or deleted. Please visit your Supabase dashboard to check project status.' 
        };
      }
      return { success: false, error: 'An unexpected error occurred during sign out' };
    }
  }

  // Request email OTP or magic link (admin email only)
  async requestOtp(email) {
    try {
      if (!email || email.toLowerCase() !== ADMIN_EMAIL.toLowerCase()) {
        return { success: false, error: 'This email is not allowed.' };
      }
      // Local dev fallback: generate a code and store it locally
      if (this.isDevEnv()) {
        const code = String(Math.floor(100000 + Math.random() * 900000));
        if (typeof window !== 'undefined') {
          window.localStorage.setItem('dev_admin_email', email);
          window.localStorage.setItem('dev_admin_code', code);
          // Surface in console for local testing
          console.warn('[DEV ONLY] Admin OTP code:', code);
        }
        return { success: true, data: { dev: true } };
      }

      const { data, error } = await supabase.auth.signInWithOtp({
        email,
        options: {
          emailRedirectTo: typeof window !== 'undefined' ? window.location.origin : undefined,
          shouldCreateUser: true,
        },
      });

      if (error) {
        return { success: false, error: error.message };
      }

      return { success: true, data };
    } catch (error) {
      if (
        error?.message?.includes('Failed to fetch') ||
        error?.message?.includes('AuthRetryableFetchError')
      ) {
        return {
          success: false,
          error:
            'Cannot connect to authentication service. Your Supabase project may be paused or inactive. Please check your Supabase dashboard and resume your project if needed.',
        };
      }
      return { success: false, error: 'Failed to send OTP' };
    }
  }

  // Verify email OTP code
  async verifyOtp(email, token) {
    try {
      if (!email || email.toLowerCase() !== ADMIN_EMAIL.toLowerCase()) {
        return { success: false, error: 'This email is not allowed.' };
      }

      // Local dev fallback: accept local code and set dev admin flag
      if (this.isDevEnv()) {
        const storedEmail = typeof window !== 'undefined' ? window.localStorage.getItem('dev_admin_email') : null;
        const storedCode = typeof window !== 'undefined' ? window.localStorage.getItem('dev_admin_code') : null;
        if (storedEmail && storedCode && storedEmail.toLowerCase() === email.toLowerCase() && String(token) === String(storedCode)) {
          try {
            window.localStorage.setItem('dev_admin', 'true');
            window.localStorage.removeItem('dev_admin_code');
          } catch {}
          return { success: true, data: { dev: true } };
        }
        return { success: false, error: 'Invalid code (dev)' };
      }

      const { data, error } = await supabase.auth.verifyOtp({
        email,
        token,
        type: 'email',
      });

      if (error) {
        return { success: false, error: error.message };
      }

      // Ensure profile exists/updated
      if (data?.user?.id) {
        await this.ensureAdminProfile(data.user.id, email);
      }

      return { success: true, data };
    } catch (error) {
      if (
        error?.message?.includes('Failed to fetch') ||
        error?.message?.includes('AuthRetryableFetchError')
      ) {
        // Clear dev admin flag then return error (previous code had unreachable cleanup)
        try {
          if (typeof window !== 'undefined') {
            window.localStorage.removeItem('dev_admin');
            window.localStorage.removeItem('dev_admin_email');
            window.localStorage.removeItem('dev_admin_code');
          }
        } catch {
          // ignore cleanup errors
        }
        return {
          success: false,
          error:
            'Cannot connect to authentication service. Your Supabase project may be paused or inactive. Please check your Supabase dashboard and resume your project if needed.',
        };
      }
      return { success: false, error: 'Invalid or expired OTP' };
    }
  }

  async ensureAdminProfile(userId, email) {
    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .upsert(
          {
            id: userId,
            email,
            full_name: email.split('@')[0],
            role: 'admin',
            updated_at: new Date().toISOString(),
          },
          { onConflict: 'id' }
        )
        .select('id')
        .single();

      if (error) {
  console.warn('ensureAdminProfile error:', error);
      }
      return { success: true, data };
    } catch (e) {
      console.warn('ensureAdminProfile catch:', e);
      return { success: false };
    }
  }

  // Get current session
  async getSession() {
    try {
      const { data, error } = await supabase.auth.getSession();

      if (error) {
        return { success: false, error: error.message };
      }

      return { success: true, data };
    } catch (error) {
      if (error?.message?.includes('Failed to fetch') || 
          error?.message?.includes('NetworkError') ||
          error?.name === 'TypeError' && error?.message?.includes('fetch')) {
        return { 
          success: false, 
          error: 'Cannot connect to authentication service. Your Supabase project may be paused or deleted. Please visit your Supabase dashboard to check project status.' 
        };
      }
      return { success: false, error: 'Failed to get session' };
    }
  }

  // Get user profile
  async getUserProfile(userId) {
    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('id', userId)
        .single();

      if (error) {
        return { success: false, error: error.message };
      }

      return { success: true, data };
    } catch (error) {
      if (error?.message?.includes('Failed to fetch') || 
          error?.message?.includes('NetworkError') ||
          error?.name === 'TypeError' && error?.message?.includes('fetch')) {
        return { 
          success: false, 
          error: 'Cannot connect to database. Your Supabase project may be paused or deleted. Please visit your Supabase dashboard to check project status.' 
        };
      }
      return { success: false, error: 'Failed to load user profile' };
    }
  }

  // Update user profile
  async updateUserProfile(userId, updates) {
    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .update({
          ...updates,
          updated_at: new Date().toISOString()
        })
        .eq('id', userId)
        .select()
        .single();

      if (error) {
        return { success: false, error: error.message };
      }

      return { success: true, data };
    } catch (error) {
      if (error?.message?.includes('Failed to fetch') || 
          error?.message?.includes('NetworkError') ||
          error?.name === 'TypeError' && error?.message?.includes('fetch')) {
        return { 
          success: false, 
          error: 'Cannot connect to database. Your Supabase project may be paused or deleted. Please visit your Supabase dashboard to check project status.' 
        };
      }
      return { success: false, error: 'Failed to update profile' };
    }
  }

  // Reset password
  async resetPassword(email) {
    try {
      const { error } = await supabase.auth.resetPasswordForEmail(email, {
        redirectTo: `${window.location.origin}/reset-password`
      });

      if (error) {
        return { success: false, error: error.message };
      }

      return { success: true };
    } catch (error) {
      if (error?.message?.includes('Failed to fetch') || 
          error?.message?.includes('AuthRetryableFetchError')) {
        return { 
          success: false, 
          error: 'Cannot connect to authentication service. Your Supabase project may be paused or inactive. Please check your Supabase dashboard and resume your project if needed.' 
        };
      }
      return { success: false, error: 'Failed to send reset email' };
    }
  }

  // Listen for auth state changes
  onAuthStateChange(callback) {
    return supabase.auth.onAuthStateChange(callback);
  }

  // Check if user is admin
  async isAdmin() {
    try {
      // Local dev admin shortcut
      try {
        if (typeof window !== 'undefined' && window.localStorage.getItem('dev_admin') === 'true') {
          return true;
        }
      } catch {
        // ignore
      }

      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        return false;
      }

      const { data, error } = await supabase
        .from('user_profiles')
        .select('role')
        .eq('id', user.id)
        .single();

      if (error || !data) {
        return false;
      }

      return data.role === 'admin';
    } catch {
      return false;
    }
  }
}

