import supabase from './supabase';
import { api } from './api/client';
const USE_API = import.meta.env.VITE_USE_API === 'true';

class BlogService {
  // Full text / heuristic search (API first, fallback to supabase includes)
  async searchPosts(q, options = {}) {
    try {
      if (!q || !q.trim()) return { success: true, data: [] };
      if (USE_API) {
        const data = await api.get('/blog/search', { q, limit: options.limit || 10 });
        return { success: true, data: data || [] };
      }
      // Fallback: use existing published post ilike search
      return await this.getPublishedPosts({ search: q, limit: options.limit || 10 });
    } catch (e) {
      return { success: false, error: 'Search failed' };
    }
  }
  // Get all published blog posts (public access)
  async getPublishedPosts(options = {}) {
    try {
      if (USE_API) {
        const data = await api.get('/blog', { published: true, search: options.search, category: options.category, tag: options.tag, limit: options.limit });
        return { success: true, data: data || [] };
      }
      let query = supabase
        .from('blog_posts')
        .select(`
          *,
          author:user_profiles(full_name, email)
        `)
        .eq('status', 'published');

      // Apply search filter
      if (options.search) {
        query = query.or(`title.ilike.%${options.search}%,excerpt.ilike.%${options.search}%,content.ilike.%${options.search}%`);
      }

      // Apply category filter
      if (options.category) {
        query = query.eq('category', options.category);
      }

      // Apply tag filter
      if (options.tag) {
        query = query.contains('tags', [options.tag]);
      }

      // Apply sorting
      switch (options.sortBy) {
        case 'oldest':
          query = query.order('published_at', { ascending: true });
          break;
        case 'most-liked':
          query = query.order('like_count', { ascending: false });
          break;
        case 'most-commented':
          query = query.order('comment_count', { ascending: false });
          break;
        case 'relevance':
          query = query.order('view_count', { ascending: false });
          break;
        default: // newest
          query = query.order('published_at', { ascending: false });
      }

      // Apply pagination
      if (options.limit) {
        query = query.limit(options.limit);
      }

      if (options.offset) {
        query = query.range(options.offset, options.offset + (options.limit || 10) - 1);
      }

  const { data, error } = await query;

      if (error) {
        return { success: false, error: error.message };
      }

      return { success: true, data: data || [] };
    } catch (error) {
      if (error?.message?.includes('Failed to fetch') || 
          error?.message?.includes('NetworkError') ||
          error?.name === 'TypeError' && error?.message?.includes('fetch')) {
        return { 
          success: false, 
          error: 'Cannot connect to database. Your Supabase project may be paused or deleted. Please visit your Supabase dashboard to check project status.' 
        };
      }
      return { success: false, error: 'Failed to load blog posts' };
    }
  }

