  // Create new blog post (admin only)
  async createPost(postData) {
    try {
      const { data: { user }, error: authErr } = await supabase.auth.getUser();
      if (!user || authErr) return { success: false, error: 'Authentication required' };
      // Generate slug from title
      const slug = (postData.slug && postData.slug.trim()) ? postData.slug.trim().toLowerCase()
        .replace(/[^a-z0-9-]+/g, '-')
        .replace(/(^-|-$)/g, '')
        : postData.title.toLowerCase()
        .replace(/[^a-z0-9]+/g, '-')
        .replace(/(^-|-$)/g, '');
      if (USE_API) {
        const token = (await supabase.auth.getSession()).data.session?.access_token;
        const res = await fetch((import.meta.env.VITE_API_BASE_URL || '/api') + '/blog', {
          method: 'POST',
          headers: { 'content-type': 'application/json', ...(token ? { Authorization: `Bearer ${token}` } : {}) },
          body: JSON.stringify({ ...postData, slug })
        });
        if (!res.ok) return { success: false, error: await res.text() };
        const data = await res.json();
        return { success: true, data };
      }
      const { data, error } = await supabase
        .from('blog_posts')
        .insert({
          ...postData,
          slug,
          author_id: user.id,
          published_at: postData.published_at ?? (postData.status === 'published' ? new Date().toISOString() : null)
        })
        .select(`
          *,
          author:user_profiles(full_name, email)
        `)
        .single();

      if (error) {
        return { success: false, error: error.message };
      }

      return { success: true, data };
    } catch (error) {
      if (error?.message?.includes('Failed to fetch') || 
          error?.message?.includes('NetworkError') ||
          error?.name === 'TypeError' && error?.message?.includes('fetch')) {
        return { 
          success: false, 
          error: 'Cannot connect to database. Your Supabase project may be paused or deleted. Please visit your Supabase dashboard to check project status.' 
        };
      }
      return { success: false, error: 'Failed to create blog post' };
    }
  }

  // Update blog post (admin only)
  async updatePost(postId, updates) {
    try {
      const { data: { user }, error: authErr } = await supabase.auth.getUser();
      if (!user || authErr) return { success: false, error: 'Authentication required' };
      const updateData = {
        ...updates,
        updated_at: new Date().toISOString()
      };

      // Update slug if title changed
      if (typeof updates.slug === 'string') {
        updateData.slug = updates.slug.trim().toLowerCase()
          .replace(/[^a-z0-9-]+/g, '-')
          .replace(/(^-|-$)/g, '');
      } else if (updates.title) {
        updateData.slug = updates.title.toLowerCase()
          .replace(/[^a-z0-9]+/g, '-')
          .replace(/(^-|-$)/g, '');
      }

      // Set published_at if status changed to published
      if (typeof updates.published_at !== 'undefined') {
        updateData.published_at = updates.published_at;
      } else if (updates.status === 'published' && !updates.published_at) {
        updateData.published_at = new Date().toISOString();
      }

      if (USE_API) {
        const token = (await supabase.auth.getSession()).data.session?.access_token;
        const url = new URL((import.meta.env.VITE_API_BASE_URL || '/api') + '/blog', window.location.origin);
        url.searchParams.set('id', postId);
        const res = await fetch(url.toString(), { method: 'PUT', headers: { 'content-type': 'application/json', ...(token ? { Authorization: `Bearer ${token}` } : {}) }, body: JSON.stringify(updateData) });
        if (!res.ok) return { success: false, error: await res.text() };
        const data = await res.json();
        return { success: true, data };
      }
      const { data, error } = await supabase
        .from('blog_posts')
        .update(updateData)
        .eq('id', postId)
        .select(`
          *,
          author:user_profiles(full_name, email)
        `)
        .single();

      if (error) {
        return { success: false, error: error.message };
      }

      return { success: true, data };
    } catch (error) {
      if (error?.message?.includes('Failed to fetch') || 
          error?.message?.includes('NetworkError') ||
          error?.name === 'TypeError' && error?.message?.includes('fetch')) {
        return { 
          success: false, 
          error: 'Cannot connect to database. Your Supabase project may be paused or deleted. Please visit your Supabase dashboard to check project status.' 
        };
      }
      return { success: false, error: 'Failed to update blog post' };
    }
  }

